import React from "react"
import styled, { keyframes } from "styled-components"

export default function ConfirmWindow ()  {

    const appearing = keyframes`
        from {  width: 0%;
          height: 0%;}
        to { width: 400px;
         height: 400px;}`

    const Window = styled.div`
    display: block;
    position: fixed;
    left: 35%;
    top: 30%;
    border-radius: 60px;
    height: 300px;
    width: 200px;
    background: #FFFFFF;
    box-shadow: 4px 4px 5px 0px rgba(181, 181, 181, 0.2),
    4px -1px 5px 0px rgba(181, 181, 181, 0.1),
    -4px 4px 20px 0px rgba(255, 255, 255, 0.3),
    inset 3px -3px 5px 0px rgba(230, 230, 230, 0.7),
    inset -3px 3px 5px 0px rgba(255, 255, 255, 0.5);

    animation: ${appearing} 0.3s ease-in-out;
    animation-fill-mode: forwards;

    `

    return <Window>
    </Window>
}
