import React from "react"
import styled, { keyframes } from "styled-components"

export default function ScaleIncreasing (points, maxPoints, background)  {
    const width = points / maxPoints * 100
    const time = width * 3.5 / maxPoints
    const animation = keyframes`
        from {  width: 0%;  }
        to { width: ${width}%; }`


    const Scale = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 50px;
    height: 90%;
    width: 0%;
    background: ${background};
    animation: ${animation} ${time}s ease-out;
    animation-fill-mode: forwards;
    `

    return <Scale/>
}

