import React, {useEffect} from "react";
import {NavLink} from "react-router-dom";
import './Appearence/index.css'
import {useHistory, useLocation} from "react-router-dom";


export const NavigationBar = () => {

    const location = useLocation()
    const currentLocation = location.pathname.slice(1)
    const history = useHistory()

    useEffect(() => {
        document.getElementById(currentLocation).style.height = '80px'
        document.getElementById(currentLocation).style.width = '80px'
    }, [currentLocation])

    const handleClick = (e) => {
        e.preventDefault()
        history.push(`/${e.target.id}`)
    }

    return (
        <div className = 'navBar'>
            <NavLink to = '/profile' className='protocircle' id = 'profile'  />
            <NavLink to = '/students' className='protocircle' id = 'students' />
            <NavLink to = '/tests' className='protocircle' id = 'tests' />

            {/*<div className='protocircle' id = 'profile' onClick = {handleClick} />*/}
            {/*<div className='protocircle' id = 'students' onClick = {handleClick}/>*/}
            {/*<div className='protocircle' id = 'tests' onClick = {handleClick}/>*/}
        </div>
    )
}
    export default NavigationBar
