import React, {useContext, useEffect, useState} from "react"
import {useParams} from "react-router-dom";
import './Appearence/index.css'
import {useHttp} from "../Hooks/http.hook"
import {useAuth} from "../Hooks/auth.hook"
import {AuthContext} from "./Context/AuthContext"

export const ResultPage = () => {

    const login = useParams().id
    const link = useParams().link

    const {request} = useHttp()
    const {token} = useContext(AuthContext)

    const [result, setResult] = useState([])
    const [test, setTest] = useState('')

    const fetchResult = async () => {
        const fetched = await request(`/api/tests/${link}/${login}`, 'GET', null, {
            Authorization: `Bearer ${token}`
        })
        setTest(fetched.test)
        setResult(fetched.scales)
        console.log(result, test)


    }

    const handleFetchingResults = async (e) => {
        e.preventDefault()
        await fetchResult()
    }

    return(
        <div>
            <h1>{test}</h1>
            <h2>Завышенные показатели</h2>
            {
                result.map((res, i) => {
                    if (res.important)
                    return (
                        <p>{res.name}</p>
                    )
                })
            }
            <h2>Норма</h2>
            {
                result.map((res, i) => {
                    if (!res.important)
                        return (
                            <p>{res.name}</p>
                        )
                })
            }
            <input type = 'button' onClick = {e => handleFetchingResults(e)}/>
        </div>
    )
}

export default ResultPage
