     const [form, setForm] = useState({ email: '', password: '' })
    const {loading, request, error} = useHttp();

     const changeHandler = event => {
         setForm({...form, [event.target.email]: event.target.value})
     }

     const registerHandler = async () => {
         try {
          const data = await request('api/auth/register', 'POST', {...form})
             console.log('Data', data)
         }
         catch (e) {}
     }
      return (
          <div class='authPage'>
              <h1>Email</h1>
              <input type='text' id='email' name='email' placeholder='Логин' onChange={changeHandler}></input>
              <input type='password' id='password' name='password' placeholder='Пароль'></input>
              <h1>Пароль</h1>
              <input type='button' value='Войти' onClick = {registerHandler} disabled={loading}></input>
          </div>
      )
}

export default Auth
