import {useState, useCallback, useEffect } from 'react'

const storageName = 'userData'

// saving the token, userID and userType in a local storage

export const useAuth = () => {
    const [token, setToken] = useState(null)
    const [ready, setReady] = useState(false)
    const [userID, setuserID] = useState(null)
    const [userType, setuserType] = useState(null)

    const login = useCallback((jwtToken, userID, userType) => {
        setToken(jwtToken)
        setuserID(userID)
        setuserType(userType)

        localStorage.setItem(storageName, JSON.stringify({ userID, token: jwtToken, userType }))

    }, [])
    const logout = useCallback(() => {
        setToken(null)
        setuserID(null)
        setuserType(null)

        localStorage.removeItem(storageName)
    }, [])

    useEffect(() => {
        const data = JSON.parse(localStorage.getItem(storageName))

        if (data && data.token) login(data.token, data.userID, data.userType)

        setReady(true)
    }, [login])

    return { login, logout, token, userID, userType, ready }
}
