import React, {useContext, useEffect, useState} from "react";
import {NavLink} from "react-router-dom";
import './Appearence/index.css'
import {useHistory, useLocation} from "react-router-dom";
import {AuthContext} from "./context/AuthContext";


export const NavigationBar = () => {

    const location = useLocation()
    const currentLocation = location.pathname.slice(1)
    const history = useHistory()
    const {userType} = useContext(AuthContext)

    useEffect(() => {
        const small = '65px'
        const big = '80px'

        changeSize('profile', small)
        changeSize('students', small)
        changeSize('tests', small)
        changeSize(currentLocation, big)

    }, [currentLocation])

    const changeSize = (element, size) => {
        document.getElementById(element).style.width = size
        document.getElementById(element).style.height = size
    }

    return (
        <div className = 'navBar'>
            <NavLink to = '/profile' className='protocircle' id = 'profile' />
            { userType !== 'u' &&
                <NavLink to = '/students' className='protocircle' id = 'students'/>
            }
            <NavLink to = '/tests' className='protocircle' id = 'tests'/>
        </div>
    )
}
    export default NavigationBar
