import React, {useContext, useEffect, useState} from "react";
import {NavLink} from "react-router-dom";
import './Appearence/index.css'
import {useHistory, useLocation} from "react-router-dom";
import {AuthContext} from "./context/AuthContext";


export const NavigationBar = () => {

    const location = useLocation()
    const currentLocation = location.pathname.slice(1)
    const history = useHistory()
    const {userType} = useContext(AuthContext)

    const [navSize, setNavSize] = useState({
        profile: '65px',
        students: '65px',
        tests: '65px'
    })

    useEffect(() => {
        navSize.profile = '65px'
        navSize.students = '65px'
        navSize.tests = '65px'
        const small = '65px'
        const big = '80px'

        document.getElementById('profile').style.width = small
        document.getElementById('profile').style.height = small

        document.getElementById('students').style.width = small
        document.getElementById('students').style.height = small

        document.getElementById('tests').style.width = small
        document.getElementById('tests').style.height = small

        document.getElementById(currentLocation).style.width = big
        document.getElementById(currentLocation).style.height = big

    }, [currentLocation])

    return (
        <div className = 'navBar'>
            <NavLink to = '/profile' className='protocircle' id = 'profile' />
            { userType !== 'u' &&
                <NavLink to = '/students' className='protocircle' id = 'students'/>
            }
            <NavLink to = '/tests' className='protocircle' id = 'tests'/>
        </div>
    )
}
    export default NavigationBar
