import React, {Component} from 'react';
import {Switch, Route, Redirect} from 'react-router-dom'
import Profile from "./Profile";
import Auth from "./Auth";
import Header from "./Header";
import NavigationBar from "./NavigationBar";
import './Appearence/index.css'
import './Appearence/Auth.css'

export const useRouts = isAuthenticated => {
    if (isAuthenticated){
        const user = 'p'
        if (user == 'p'){
            return (
                <Switch>
                    <Route path = '/profile' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                                <Profile/>
                            </div>
                        </div>
                    </Route>
                    <Route path = '/students' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                            </div>
                        </div>
                    </Route>
                    <Route path = '/tests' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                            </div>
                        </div>
                    </Route>
                    {/*<Redirect to = '/profile'/>*/}
                </Switch>
            )
        }
        else if (user == 'u'){
            return (
                <Switch>
                    <Route path = '/profile' exact> <Header/><h1>Ученик</h1><NavigationBar/><Profile/> </Route>
                    <Route path = '/tests' exact> <Header/> <NavigationBar/> </Route>
                    <Redirect to = '/profile'/>
                </Switch>
            )
        }
        else if (user == 'a'){
            return (
                <Switch>
                    <Route path = '/profile' exact> <Header/><h1>Админ</h1><NavigationBar/><Profile/> </Route>
                    <Route path = '/tests' exact> <Header/> <NavigationBar/> </Route>
                    <Route path = '/users' exact> <Header/> <NavigationBar/> </Route>
                    <Redirect to = '/profile'/>
                </Switch>
            )
        }
    }
    else {
        return (
            <Switch>
                <Route path = '/' exact> <Auth/> </Route>
                <Redirect to = '/profile'/>
            </Switch>
        )
    }
}
