import React, {useContext} from 'react';
import {Switch, Route, Redirect} from 'react-router-dom'
import Profile from "./Profile";
import Auth from "./Auth";
import Header from "./Header";
import NavigationBar from "./NavigationBar";
import './Appearence/index.css'
import './Appearence/Auth.css'
import Students from "./Students";
import Tests from "./Tests";
import {TestPage} from "./TestPage";

import {AuthContext} from "./Context/AuthContext";

// from App.js (Client)
export const useRouts = (isAuthenticated, type, username) => {

    if (isAuthenticated){

        const auth = useContext(AuthContext)
        // const data = localStorage.getItem('userData')
        alert(auth)

        if (type === 'p'){
            return (
                <Switch>
                    <Route path = '/profile/:id' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                                <Profile/>
                            </div>
                        </div>
                    </Route>

                    <Route path = '/students' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                                <Students/>
                            </div>
                        </div>
                    </Route>

                    <Route path = '/tests' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                                <Tests/>
                            </div>
                        </div>
                    </Route>

                    <Route path = '/tests/:id' className = 'greenPage' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                                <TestPage/>
                            </div>
                        </div>
                    </Route>
                    {/*<Redirect to = {'/profile/'+ username}/>*/}
                    <Redirect to = '/students'/>
                </Switch>
            )
        }
        // if (type === 'p') {
        //     return (
        //         <Switch>
        //             <Route path = '/profile' exact>
        //                 <div className = 'page'>
        //                     <Header/>
        //                     <NavigationBar/>
        //                     <div className='pageContent'>
        //                         <Profile/>
        //                     </div>
        //                 </div>
        //             </Route>
        //             <Route path = '/students' exact>
        //                 <div className = 'page'>
        //                     <Header/>
        //                     <NavigationBar/>
        //                     <div className='pageContent'>
        //                         <Students/>
        //                     </div>
        //                 </div>
        //             </Route>
        //             <Route path = '/tests' exact>
        //                 <div className = 'page'>
        //                     <Header/>
        //                     <NavigationBar/>
        //                     <div className='pageContent'>
        //                         <Tests/>
        //                     </div>
        //                 </div>
        //             </Route>
        //             <Route path = '/tests/:id' className = 'greenPage'>
        //                 <div className = 'page'>
        //                     <Header/>
        //                     <NavigationBar/>
        //                     <div className='pageContent'>
        //                         <TestPage/>
        //                     </div>
        //                 </div>
        //             </Route>
        //             <Redirect to = '/profile'/>
        //         </Switch>
        //     )
        // }
        else if (type === 'a') {
            return (
            <Switch>
                <Route path = '/profile/:id' exact>
                    <div className = 'page'>
                        <Header/>
                        <NavigationBar/>
                        <div className='pageContent'>
                            <Profile/>
                        </div>
                    </div>
                </Route>

                <Route path = '/students' exact>
                    <div className = 'page'>
                        <Header/>
                        <NavigationBar/>
                        <div className='pageContent'>
                            <Students/>
                        </div>
                    </div>
                </Route>

                <Route path = '/tests' exact>
                    <div className = 'page'>
                        <Header/>
                        <NavigationBar/>
                        <div className='pageContent'>
                            <Tests/>
                        </div>
                    </div>
                </Route>

                <Route path = '/tests/:id' className = 'greenPage'>
                    <div className = 'page'>
                        <Header/>
                        <NavigationBar/>
                        <div className='pageContent'>
                            <TestPage/>
                        </div>
                    </div>
                </Route>

                <Redirect to = {'/profile/'+ username}/>
            </Switch>
            )
        }
        else if (type === 'u') {
            return (
                <Switch>
                    <Route path = {'/'+ username} exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                                <Profile/>
                            </div>
                        </div>
                    </Route>
                    <Route path = '/profile' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                                <Profile/>
                            </div>
                        </div>
                    </Route>
                    <Route path = '/tests' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                                <Tests/>
                            </div>
                        </div>
                    </Route>
                    <Route path = '/tests' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                                <Tests/>
                            </div>
                        </div>
                    </Route>

                    <Redirect to = {'/profile/'+ username}/>
                </Switch>
            )
        }
    }
    else {
        return (
            <Switch>
                <Route path = '/' exact>
                    <div className = 'authPageGr'>
                        <Auth/>
                    </div>
                </Route>

                <Redirect to = '/'/>
            </Switch>
        )
    }
}
