import React, {useContext, useEffect, useState} from "react";
import {NavLink} from "react-router-dom";
import './Appearence/index.css'
import {useHistory, useLocation} from "react-router-dom";
import {AuthContext} from "./context/AuthContext";


export const NavigationBar = () => {

    const location = useLocation()
    const currentLocation = location.pathname.slice(1)
    const history = useHistory()
    const {userType} = useContext(AuthContext)

    const [navSize, setNavSize] = useState({
        profile: '65px',
        students: '65px',
        tests: '65px'
    })

    useEffect(() => {
        navSize.profile = '65px'
        navSize.students = '65px'
        navSize.tests = '65px'
        const small = 'width: 65px; height: 65px;'
        const big = 'width: 80px; height: 80px;'

        if (currentLocation === 'profile')
            setNavSize({
                profile: big,
                students: small,
                tests: small
            })
        else if (currentLocation === 'students')
            setNavSize({
                profile: small,
                students: big,
                tests: small
            })
        else if (currentLocation === 'tests')
            setNavSize({
                profile: small,
                students: small,
                tests: big
            })
    }, [currentLocation])

    return (
        <div className = 'navBar'>
            <NavLink to = '/profile' className='protocircle' id = 'profile' style = {navSize.profile} />
            { userType !== 'u' &&
                <NavLink to = '/students' className='protocircle' id = 'students' style = {navSize.students}/>
            }
            <NavLink to = '/tests' className='protocircle' id = 'tests' style = {navSize.tests}/>
        </div>
    )
}
    export default NavigationBar
