import React, {useCallback, useContext, useEffect, useState} from 'react';
import './Appearence/index.css'
import './Appearence/Tests.css'
import {useHttp} from "../Hooks/http.hook";
import {AuthContext} from "./Context/AuthContext";
import {NavLink} from "react-router-dom";
import {ReactComponent as SortIcon} from './Appearence/Images/SortIcon.svg'
import {ReactComponent as AddIcon} from "./Appearence/Images/AddIcon.svg";

export const Tests = () => {

    const [tests, setTests] = useState([])
    const {token} = useContext(AuthContext)
    const {loading, request, error, clearError} = useHttp();
    const [shown, setShown] = useState([])
    const [search, setSearch] = useState([])
    const [notFound, setNotFound] = useState()
    const [sorted, setSorted] = useState(false)
    const [isSearching, activateSearching] = useState('none')
    const [isAdding, activateAdding] = useState('none')

    const fetchTests = useCallback(async () => {
        try {
            const fetched = await request('/api/tests', 'GET', null, {
                Authorization: `Bearer ${token}`
            })
            setTests(fetched)
        } catch (e) {}
    }, [token, request])


    const handleSearchActivating = e => {
        e.preventDefault()
        switch (isSearching){
            case 'active':
                activateSearching('inactive');
                break
            case 'none':
            case 'inactive':
                activateSearching('active');
                document.getElementById('search').focus()
                break
        }
    }

    const doSearch = e => {
        e.preventDefault()
        try {
            let searchFilter = e.target.value.toString().toLowerCase()
            setSearch(searchFilter)
            setNotFound(false)
            if (searchFilter != '') {
                let testsArray = []
                tests.map(test => {
                    testsArray.push((test.name + test.questions + test.description).toString().toLowerCase())
                })

                let indexes = []
                testsArray.map((test, i) => {
                    if (test.toString().includes(searchFilter))
                        indexes.push(i)
                })

                if (indexes.length != 0){
                    setShown([...indexes])
                    setNotFound(false)
                }
                else setNotFound(true)
            }
            else showAll()
        }
        catch (e) {}
    }

    const doSort = e => {
        e.preventDefault()
        try {
            switch (sorted) {
                case true:
                    document.getElementById('sortIcon').style = 'transform: rotate(360deg);';
                    document.getElementById('testsCollection').style = 'flex-direction: row;';
                    setSorted(false);
                    break;
                case false:
                    document.getElementById('sortIcon').style = 'transform: rotate(180deg);';
                    document.getElementById('testsCollection').style = 'flex-direction: row-reverse;';
                    setSorted(true);
                    break;
            }
        }
        catch (e) {}
    }

    const showAll = () => {
        let shownSize = []
        for (let i = 0; i < tests.length; i++){
            shownSize.push(i)
        }
        setShown([...shownSize])
    }

    const declension = questions => {
        const q = questions.toString().split((questions.toString().length ))
        switch (q) {
            case 1: return 'вопрос'; break;
            case 2:
            case 3:
            case 4: return 'вопроса'; break;
            case 5:
            case 6:
            case 7:
            case 8:
            case 9:
            case 0: return 'вопросов'; break;
        }

    }


    useEffect(() => {
        if (tests.length == 0) fetchTests()
        document.body.style = 'transition-duration: .7s; transition-timing-function: ease-in-out; background: white;'

        if (shown.length == 0)
            showAll()
    }, [fetchTests, shown, showAll])

    return (
        <div>
            <h1 className = 'soft_appearing'>Методики</h1>


            <div className = 'searchSortAndAdd'>
                <input type = 'text' id = 'search'
                       className = {  isSearching === 'active' && 'searchAppearing' ||
                       isSearching === 'inactive' && 'searchDisappearing' ||
                       isSearching === 'none' && 'invisible'}
                       placeholder = 'Поиск по ученикам и классам'onChange = {doSearch}/>

                <svg className = 'searchIcon' name = 'searchIcon' width="250px" height="277px" viewBox="0 0 250 277" version="1.1"
                     onClick = {handleSearchActivating}>
                    <g id="Logo" stroke="none" stroke-width="1" fill="none" fill-rule="evenodd">
                        <path className = {   isSearching === 'active' && 'activeStroke activeFill' ||
                        isSearching === 'inactive' && 'inactiveStroke inactiveFill' ||
                        isSearching === 'none' && 'inactiveStroke inactiveFill'}
                              d="M256.23536,245.633373 C256.240323,245.854751 256.242819,246.076789 256.242819,246.299457 C256.242819,261.211145 245.049938,273.299457 231.242819,273.299457 C217.4357,273.299457 206.242819,261.211145 206.242819,246.299457 C206.242819,246.076789 206.245315,245.854751 206.250278,245.633373 Z" id="Combined-Shape" transform="translate(231.242819, 259.466415) rotate(-40.000000) translate(-231.242819, -259.466415) "></path>
                        <rect id="Rectangle"
                              className = {   isSearching === 'active' && 'activeStroke activeFill' ||
                              isSearching === 'inactive' && 'inactiveStroke inactiveFill' ||
                              isSearching === 'none' && 'inactiveStroke inactiveFill'}
                              transform="translate(199.856726, 222.096267) rotate(-40.000000) translate(-199.856726, -222.096267) " x="174.856726" y="186.596267" width="50" height="71"></rect>
                        <circle id="Oval"
                                className = {   isSearching === 'active' && 'activeStroke' ||
                                isSearching === 'inactive' && 'inactiveStroke' ||
                                isSearching === 'none' && 'inactiveStroke'}
                                stroke-width="50" cx="125" cy="125" r="100"></circle>
                    </g>
                </svg>


                {/*<SearchIcon onClick = {e => handleSearchActivating(e)} isSearching = {isSearching}/>*/}
                <SortIcon onClick = {e => doSort(e)}/>

                <NavLink to = '/addtest'>
                    <AddIcon/>
                </NavLink>



            </div>

            {/*<input type='button' className = 'createbtn' value='+' onClick = {createTest} disabled={loading}/>*/}
            <div id = 'testsCollection' className = 'tests'>
                {notFound && <h1 className = 'notFound'>Ничего не найдено</h1>}
            {
                !notFound &&
                tests.map((test, index) => {
                if (shown.includes(index))
                return (
                    <NavLink to = {'/tests/' + test.link} key = {index} className = 'testItem soft_appearing'>
                        <p className = 'testName'>{test.name}</p>
                        <p className = 'questions'>{test.q} {declension(test.q)}</p>
                        <p className = 'description'>{test.shortDescription}</p>
                    </NavLink>
                )
            })}
            </div>
        </div>
    )

}

export default Tests
