import * as cyrToTranslit from 'cyrillic-to-translit-js'

const createShortLink = name => {
    let shortLink = cyrToTranslit().transform(name).toString().replace(/[^А-Яа-яA-Za-z0-9]+/gi, '-').toLowerCase()
    return shortLink
}

export default createShortLink
