import React, {Component} from 'react';
import {Switch, Route, Redirect} from 'react-router-dom'
import Profile from "./Profile";
import Auth from "./Auth";
import Header from "./Header";
import NavigationBar from "./NavigationBar";

export const useRouts = (isAuthenticated, user = 'p') => {
    if (isAuthenticated){
        if (user == 'p')
        return (
            <Switch>
                <Route path = '/profile' exact> <Header/><NavigationBar/><Profile/> </Route>
                <Route path = '/tests' exact> <Header/> <NavigationBar/> </Route>
                <Route path = '/students' exact> <Header/> <NavigationBar/> </Route>
                <Redirect to = '/profile'/>
            </Switch>
        )
    }
    else {
        return (
            <Switch>
                <Route path = '/' exact> <Auth/> </Route>
                <Redirect to = '/profile'/>
            </Switch>
        )
    }
}
