import './Appearence/index.css'
import {useHistory, useLocation} from "react-router-dom";


export const NavigationBar = () => {

    const location = useLocation()
    const currentLocation = location.pathname.slice(1)
    const history = useHistory()
    const elements = document.getElementById('hey').childNodes
    elements.forEach(element => makeSmaller(element))
    makeBigger(document.getElementById(currentLocation))


    const handleClick = (e) => {
        e.preventDefault()
        history.push(`/${e.target.id}`)
    }
