import React, {useEffect, useState} from "react";
import {NavLink} from "react-router-dom";
import './Appearence/index.css'
import {useHistory, useLocation} from "react-router-dom";


export const NavigationBar = () => {

    const location = useLocation()
    const currentLocation = location.pathname.slice(1)
    const history = useHistory()

    const [navSize, setNavSize] = useState({
        profile: '65px',
        students: '65px',
        tests: '65px'
    })

    useEffect(() => {
        // document.getElementById(currentLocation).style.height = '80px'
        // document.getElementById(currentLocation).style.width = '80px'
        navSize.profile = '65px'
        navSize.students = '65px'
        navSize.tests = '65px'
        if (currentLocation == 'profile')
            navSize.profile = '80px'
        else if (currentLocation == 'students')
            navSize.students = '80px'
        else if (currentLocation == 'tests')
            navSize.tests = '80px'
    }, [currentLocation])

    return (
        <div className = 'navBar'>
            <NavLink to = '/profile' className='protocircle' id = 'profile' width = {navSize.profile} height = {navSize.profile} />
            <NavLink to = '/students' className='protocircle' id = 'students' width = {navSize.students} height = {navSize.students}/>
            <NavLink to = '/tests' className='protocircle' id = 'tests' width = {navSize.tests} height = {navSize.tests}/>
        </div>
    )
}
    export default NavigationBar
