  const {
    token
  } = useContext(AuthContext);
  const {
    request
  } = useHttp();
  const [errors, setErrors] = useState({
    name: '',
    link: ''
  });
  const [saveBtnClasses, setSaveBtnClasses] = useState({
    test: 'invisible'
  });
  const [testData, setTestData] = useState({
    name: '',
    link: '',
    shortDescription: '',
    description: '',
    scales: [{
      name: '',
      intervals: [{
        name: '',
        description: '',
        min: '',
        max: '',
        important: false
      }],
      scaleanswers: [{
        k: '',
        questions: []
      }]
    }],
    questions: [''],
    answers: ['']
  });

  const handleSaveBtnClasses = (e, test) => {
    e.preventDefault();
    let testClass;
    if (!test) testClass = 'invisible';else testClass = 'slow_appearing';
    setSaveBtnClasses({
      test: testClass
    });
  };

  const isImportantBtn = isImportant => {
    return classNames({
      inactiveImportantBtn: !isImportant,
      activeImportantBtn: isImportant
    });
  };

  const saveDraftBtnClasses = isImportant => {
    return classNames({
      inactiveImportantBtn: !isImportant,
      activeImportantBtn: isImportant
    });
  };

  const textStatus = isActive => {
    return classNames({
      inactiveText: !isActive,
      activeText: isActive
    });
  }; // const fetchDrafts = useCallback(async() => {
  //     try {
  //         const fetched = await request('addtest/drafts', 'GET', null, {
  //             Authorization: `Bearer ${token}`
  //         })
  //         setDrafts(fetched)
  //     }
  //     catch (e) {}
  // }, [token, request])


  const fetchTest = useCallback(async () => {
    try {
      const fetched = await request(`/api/tests/${testlink}`, 'GET', null, {
        Authorization: `Bearer ${token}`
      });
      console.log(fetched);
      setTestData(fetched);
    } catch (e) {}
  }, [token, request]);

  const handleSaving = async (e, type) => {
    e.preventDefault();

    switch (type) {
      case 'test':
        await saveTest();
        break;

      case 'draft':
        await saveTestDraft();
        break;
    }
  };

  const saveTestDraft = async () => {
    if (await checkFields(false)) {
      console.log(testData);
      const data = await request('addtest/savedraft', 'POST', { ...testData,
        date: new Date()
      });

      if (data.message === 'Draft is saved') {
        notify('Черновик сохранён');
        setTestData({ ...testData,
          _id: data.id
        });
        fetchDrafts();
      }
    }
  };

  const saveTest = async () => {
    if (await checkFields(true)) {
      console.log(testData);
      const data = await request('addtest/savetest', 'POST', { ...testData,
        date: new Date()
      });

      if (data.message === 'Test is saved') {
        notify('Тест сохранён');
        setTestData({ ...testData,
          _id: data.id
        });
      }
    }
  };

  const checkFields = async doubleCheck => {
    let permission = false;

    try {
      let name = '',
          link = '';

      if (testData.name && !RegExps.testNameRegExp.test(testData.name)) {
        name = 'Допустима только кириллица, пробелы и знаки препинания';
        await showError(true, 'testName', 'errorTestName');
      } else if (!testData.name) {
        name = 'Введите название методики';
        !doubleCheck && notify('Введите название методики, чтобы сохранить черновик');
        await showError(true, 'testName', 'errorTestName');
      }

      if (testData.link && !RegExps.linkRegExp.test(testData.link)) {
        if (name) {
          testData.link = '';
        } else if (!name) {
          link = 'Допустимы только буквы a-z, цифры и дефис между ними';
          await showError(true, 'testLink', 'errorTestLink');
        }
      } else if (!testData.link) {
        if (!name) testData.link = createShortLink(testData.name);else testData.link = '';
      }

      await setErrors({
        name,
        link
      });
