import React from "react"
import { keyframes, css } from "styled-components"

export const ScaleIncreasing = (points, maxPoints) => {
    const width = points/maxPoints * 100
    console.log(width)
    const animation = keyframes`
        from {  width: 0%;  }
        to { width: ${width}%; }`

    const style = css`
        animation: ${animation} 2s ease-out;
        animation-fill-mode: forwards;
    `

    return 'hi'
}

export default ScaleIncreasing
