                {/*                                <input type = 'text' id = 'intervalName' name = 'name' value = {interval.name} placeholder = {'Интервал ' + (j + 1)} onChange = { e => changeIntervalHandler(e, i, j) }/>*/}

                {/*                                <p id = 'intervalP'>Интервал означает нарушение, выход за пределы нормы?</p>*/}

                {/*                                <div id = 'isImportantYesContainer'>*/}
                {/*                                    <div id = 'isImportantYes' className = { isImportantBtn(interval.important) } onClick = { e => changeIntervalHandler(e, i, j) }/>*/}
                {/*                                    <p onClick = { e => changeIntervalHandler(e, i, j) }>Да</p>*/}
                {/*                                </div>*/}

                {/*                                <div id = 'isImportantNoContainer'>*/}
                {/*                                    <div id = 'isImportantNo' className = { isImportantBtn(!interval.important) } onClick = { e => changeIntervalHandler(e, i, j) }/>*/}
                {/*                                    <p onClick = { e => changeIntervalHandler(e, i, j) }>Нет</p>*/}
