                {/*                                <textarea id = 'intervalDescription' name = 'description' value = {interval.description} placeholder = 'Комментарий' onChange = { e => changeIntervalHandler(e, i, j) }/>*/}

                {/*                                <p id = 'intervalP2'>Сколько баллов по шкале нужно набрать, чтобы попасть в интервал?</p>*/}

                {/*                                <div id = 'intervalMin'>*/}
                {/*                                    <p>От </p>*/}
                {/*                                    <input type = 'number' name = 'min' value = {interval.min} placeholder = 'баллов' onChange = { e => changeIntervalHandler(e, i, j) }/>*/}
