                {/*    </div>*/}

                {/*</div>*/}

                <div id = 'scaleAddingIcon' onClick = { e => handleAddingScale(e) }>
                    <AddIcon title = 'Новая шкала'/>
                    <p>Ещё одна шкала</p>
                </div>
            </div>

            <div className = 'addingTestModule'>
            <h2>Вопросы</h2>
            </div>

            <div className = 'addingTestModule'>
                <p>Варианты ответов:</p>
            </div>

            <div className = 'addingTestModule'>
                <div className = 'answersAdd'>
                    {
                        testData.answers.map((answer, i) => {
                            return(
                                <div className = 'questionAdd' key = {i}>
                                    <input type = 'text' name = {'a' + i} id = {'a' + i} placeholder = {'Вариант ответа ' + i} value = {testData.answers['a' + i]} onChange = { e => changeAnswersHandler(e, i) }/>
                                    <CancelIcon onMouseEnter = {e => colorBackground(e, 'a', i, '', 'red')} onMouseLeave = {e => colorBackground(e, 'a', i, '', 'white')} onClick = { e => handleDeletingAnswer(e, i) } titile = 'Убрать этот вариант ответа'/>
                                </div>)
                        })
                    }
                </div>
                <div onClick = { e => handleAddingAnswer(e) }>
