import React, {useContext, Component, useEffect} from 'react';
import './Appearence/index.css'
import {AuthContext} from "./Context/AuthContext";
import {useHistory} from "react-router-dom";

export const Header = () => {

    const history = useHistory()
    const {logout} = useContext(AuthContext)

    const logoutHandler = e => {
      e.preventDefault()
      logout()
      history.push('/')
    }

    return (
        <div className = 'headerZone'>
          <div className = 'logoutbtn' title = 'Выйти' onClick = {e => logoutHandler(e)}>
              <a href = '/'/>
              <svg width="15px" height="20px" viewBox="0 0 15 20" version="1.1">
                  <g id="Logo" stroke="none" stroke-width="1" fill="none" fill-rule="evenodd">
                      <polygon id="Triangle" fill="#FFFFFF" transform="translate(7.500000, 10.000000) rotate(-90.000000) translate(-7.500000, -10.000000) " points="7.5 2.5 17.5 17.5 -2.5 17.5"></polygon>
                  </g>
              </svg>
          </div>
        </div>
    );
}
  export default Header
