import React, {useCallback, useContext, useEffect, useState} from 'react'
import './Appearence/index.css'
import './Appearence/TestPage.css'
import {AuthContext} from "./Context/AuthContext";
import {useHttp} from "../Hooks/http.hook";
import {useParams} from 'react-router-dom'

export const TestPage = () => {

    const [test, setTest] = useState({ name: '', description: '', shortDescription: ''})
    const [questions, setQuestions] = useState({})
    const {token, userID} = useContext(AuthContext)
    const [answers, setAnswers] = useState({userID: userID})
    const {loading, request, error, clearError} = useHttp();
    const link = useParams().id

    const fetchTestData = useCallback(async () => {
        try {
            const fetched = await request(`/api/tests/${link}`, 'GET', null, {
                Authorization: `Bearer ${token}`
            })
            setTest({name: fetched.tests.name, description: fetched.tests.description, shortDescription: fetched.tests.shortDescription})
            setQuestions(fetched.questions)
            setAnswers({...answers, testID: fetched.questions.testID})
        }
        catch (e) {}
    }, [token, request])

    useEffect(() => {
        fetchTestData()
    }, [fetchTestData])

    const yesHandler = e => {
        e.preventDefault()
        // console.log("ON THE " + e.target.id + "th answer you said yes")
        setAnswers({...answers, ["a" + e.target.id]: true })
    }

    const noHandler = e => {
        e.preventDefault()
        // console.log("ON THE " + e.target.id + "th answer you said no")
        setAnswers({...answers, ["a" + e.target.id]: false })
    }

    const saveAnswers = async () => {
        try {
            console.log("ANSWERSS ARE ", answers)
            const data = await request(`${link}/save`, 'POST', {...answers}, {
                Authorization: `Bearer ${token}`
            })
            console.log(data.message)
        }
        catch (e) {}
    }


    return (
        <div>
            <h1 cls>{test.name}</h1>
            <p className = 'testInfo'>{test.shortDescription}</p>
            <p className = 'testInfo'>{test.description}</p>
            <input type='button' className = 'yesbtn'  value = 'погнали' disabled={loading} onClick={doTest}/>
            {
                Object.keys(questions).map((key, index) =>
                    key !== '_id' && key !== 'testID' &&
                    <div key = {index} className = 'questionCard' >
                        <p value={key}>{ questions[key] } </p>
                        <input type='button' className = 'yesbtn'  value = 'да' id = {index} disabled={loading} onClick={yesHandler}/>
                        <input type='button' className = 'nobtn'  value = 'нет' id = {index} disabled={loading} onClick={noHandler}/>
                    </div>
                )
            }
            <input type='button' className = 'yesbtn'  value = 'результат' disabled={loading} onClick={saveAnswers}/>
        </div>


    )
}
