import React, {useCallback, useContext, useEffect, useState} from 'react';
import './Appearence/index.css'
import './Appearence/Students.css';
import {useHttp} from "../Hooks/http.hook";
import {AuthContext} from "./Context/AuthContext";
import {NavLink} from "react-router-dom";


export const Students = () => {

    const {loading, request, error, clearError} = useHttp();

    const [form, setForm] = useState({
        login: '',
        password: '',
        type: '',
        pic: '',
        name: '',
        surname: '',
        grade: null,
        letter: '',
        birthdate: ''
    })
    const [students, setStudents] = useState([])
    const [shown, setShown] = useState([])
    const [isSearching, activateSearching] = useState('none')
    const [search, setSearch] = useState('')
    const {token} = useContext(AuthContext)

    const changeHandler = event => {
        setForm({...form, [event.target.name]: event.target.value})
    }

    const registerHandler = async () => {
        try {
            const data = await request('api/students/create', 'POST', {...form})
            console.log(data.message)
            if (data.message === 'User\'ve created'){
                setForm({
                    login: '',
                    password: '',
                    type: '',
                    pic: '',
                    name: '',
                    surname: '',
                    grade: null,
                    letter: '',
                    birthdate: ''
                })
            }
        }
        catch (e) {}
    }

    const fetchStudents = useCallback(async () => {
        try {
            const fetched = await request('/api/students', 'GET', null, {
                Authorization: `Bearer ${token}`
            })
            fetched.sort((a, b) => a.grade > b.grade ? 1 : -1);
            setStudents(fetched)

        } catch (e) {}
    }, [token, request])

    const handleSearchActivating = e => {
        e.preventDefault()
        switch (isSearching){
            case 'active':
                activateSearching('inactive');
                break
            case 'none':
            case 'inactive':
                activateSearching('active');
                document.getElementById('search').focus()
                break
        }
    }

    const setSearchFilter = e => {
        e.preventDefault()
        let search = e.target.value.toString().toLowerCase()
        setSearch(search)
        if (search != '')
            doSearch()
        else showAll()
    }

        const doSearch = e => {
            try {
                if (search != '') {
                    let studentsArray = []
                    students.map(student => {
                        studentsArray.push((student.name + student.surname).toString().toLowerCase())
                    })

                    let indexes = []
                    studentsArray.map((student, i) => {
                        if (studentsArray[i].toString().includes(search))
                            indexes.push(i)
                    })

                    if (indexes.length != 0)
                        setShown([...indexes])
                    else showAll()
                }
                else showAll()
            }
            catch (e) {}
        }

        const showAll = () => {
            let shownSize = []
            for (let i = 0; i < students.length; i++){
                shownSize.push(i)
            }
            setShown([...shownSize])
        }






    useEffect(() => {
        fetchStudents()
        document.body.style = 'transition-duration: .5s; transition-timing-function: ease-in-out; background: white;'
        // console.log('😊'.codePointAt())
        // console.log(String.fromCodePoint(128522))
        if (shown.length == 0) showAll()
    }, [fetchStudents, showAll])

    return(
        <div>
            <h1>Ученики</h1>
            <div className = 'searchAndSort'>
                <input type = 'text' id = 'search'
                       className = {  isSearching === 'active' && 'searchAppearing' ||
                       isSearching === 'inactive' && 'searchDisappearing' ||
                       isSearching === 'none' && 'invisible'}
                       placeholder = 'Поиск по ученикам и классам' onChange = {setSearchFilter}/>

                <svg className = 'searchIcon' name = 'searchIcon' width="250px" height="277px" viewBox="0 0 250 277" version="1.1"
                     onClick = {handleSearchActivating}>
                    <g id="Logo" stroke="none" stroke-width="1" fill="none" fill-rule="evenodd">
                        <path className = {   isSearching === 'active' && 'activeStroke activeFill' ||
                        isSearching === 'inactive' && 'inactiveStroke inactiveFill' ||
                        isSearching === 'none' && 'inactiveStroke inactiveFill'}
                              d="M256.23536,245.633373 C256.240323,245.854751 256.242819,246.076789 256.242819,246.299457 C256.242819,261.211145 245.049938,273.299457 231.242819,273.299457 C217.4357,273.299457 206.242819,261.211145 206.242819,246.299457 C206.242819,246.076789 206.245315,245.854751 206.250278,245.633373 Z" id="Combined-Shape" transform="translate(231.242819, 259.466415) rotate(-40.000000) translate(-231.242819, -259.466415) "></path>
                        <rect id="Rectangle"
                              className = {   isSearching === 'active' && 'activeStroke activeFill' ||
                              isSearching === 'inactive' && 'inactiveStroke inactiveFill' ||
                              isSearching === 'none' && 'inactiveStroke inactiveFill'}
                              transform="translate(199.856726, 222.096267) rotate(-40.000000) translate(-199.856726, -222.096267) " x="174.856726" y="186.596267" width="50" height="71"></rect>
                        <circle id="Oval"
                                className = {   isSearching === 'active' && 'activeStroke' ||
                                isSearching === 'inactive' && 'inactiveStroke' ||
                                isSearching === 'none' && 'inactiveStroke'}
                                stroke-width="50" cx="125" cy="125" r="100"></circle>
                    </g>
                </svg>

                {/*<svg className = 'sortIcon' id = 'sortIcon' onClick = {doSort} width="378px" height="282px" viewBox="0 0 378 282" version="1.1">*/}
                {/*    <g id="Logo" stroke="none" stroke-width="1" fill="none" fill-rule="evenodd">*/}
                {/*        <path d="M96.0389392,1 C106.609669,1 115.859436,5.58308193 120.9273,12.4308996 L121.015825,12.5525321 L184.479,88.185 L184.496508,88.1714212 C184.642609,88.3378163 184.787245,88.5063032 184.930373,88.6768766 C194.515421,100.099893 193.711394,116.554722 183.134527,125.429767 C172.70048,134.184972 156.653546,132.29306 147.020097,121.273869 L146.992363,121.296357 L121.038,90.365 L121.038939,199 L121.03148,199 C121.036443,199.221378 121.038939,199.443416 121.038939,199.666084 C121.038939,214.577772 109.846058,226.666084 96.0389392,226.666084 C82.2318205,226.666084 71.0389392,214.577772 71.0389392,199.666084 C71.0389392,199.443416 71.041435,199.221378 71.0463979,199 L71.0389392,199 L71.038,90.942 L46.409,120.293 L46.4188636,120.301213 C46.2803665,120.473988 46.1395549,120.645684 45.9964268,120.816257 C36.4113784,132.239273 20.0669169,134.304812 9.49005029,125.429767 C-0.91459931,116.699229 -1.8620698,100.633732 7.23426161,89.2388073 L7.21214233,89.2211541 L69.5356122,14.9464184 C74.0533978,6.728782 84.2205763,1 96.0389392,1 Z" id="Combined-Shape"*/}
                {/*              stroke="#4A4A4A" fill="#4A4A4A"></path>*/}
                {/*        <path d="M282.185638,55 C294.279566,55 304.644412,60.9990492 308.99572,69.5252612 L370.259061,142.536 L370.296508,142.505337 C370.442609,142.671732 370.587245,142.840219 370.730373,143.010793 C380.315421,154.433809 379.511394,170.888638 368.934527,179.763683 C358.495096,188.523405 342.436985,186.625021 332.805192,175.590722 L332.757304,175.630273 L306.803061,144.699 L306.80388,253.333916 L306.796421,253.333916 C306.801384,253.555294 306.80388,253.777332 306.80388,254 C306.80388,268.911688 295.610999,281 281.80388,281 C267.996761,281 256.80388,268.911688 256.80388,254 C256.80388,253.777332 256.806376,253.555294 256.811339,253.333916 L256.80388,253.333916 L256.803061,145.276 L232.172061,174.629 L232.179924,174.635129 C232.041427,174.807904 231.900616,174.9796 231.757488,175.150173 C222.172439,186.573189 205.827978,188.638728 195.251111,179.763683 C184.845967,171.03273 183.898901,154.96612 192.99662,143.571098 L192.977083,143.55507 L255.890594,68.5762614 C260.507198,60.5572584 270.545545,55 282.185638,55 Z" id="Combined-Shape-Copy-10"*/}
                {/*              stroke="#D6D6D6" fill="#D6D6D6" transform="translate(282.092819, 168.000000) rotate(180.000000) translate(-282.092819, -168.000000) "></path>*/}
                {/*    </g>*/}
                {/*</svg>*/}



            </div>
            {/*<div className = 'authform'>*/}
            {/*    <input type='text' id = 'login' name='login' placeholder='Логин' onChange={changeHandler} value = {form.login}/>*/}
            {/*    <input type='text' id = 'password' name='password' placeholder='Пароль' onChange={changeHandler} value = {form.password}/>*/}
            {/*    <input type='text' id = 'type' name='type' placeholder='Тип' onChange={changeHandler} value = {form.type}/>*/}
            {/*    <input type='text' id = 'name' name='name' placeholder='Имя' onChange={changeHandler} value = {form.name}/>*/}
            {/*    <input type='text' id = 'surname' name='surname' placeholder='Фамилия' onChange={changeHandler} value = {form.surname}/>*/}
            {/*    { form.type === 'u' &&*/}
            {/*        <input type='text' id = 'grade' name='grade' placeholder='Класс' onChange={changeHandler} value = {form.grade}/>}*/}
            {/*    { form.type === 'u' &&*/}
            {/*        <input type='text' id = 'letter' name='letter' placeholder='Буква' onChange={changeHandler} value = {form.letter}/>}*/}
            {/*    { students.map((student) => {*/}
            {/*        return (*/}
            {/*            <div key = {student._id} className = 'authform'>*/}
            {/*                /!*<h2>{student._id}</h2>*!/*/}
            {/*                <h2>{student.grade}{student.letter}</h2>*/}
            {/*                <h2>{student.name} {student.surname}</h2>*/}
            {/*            </div>*/}
            {/*        )*/}
            {/*    }) }*/}
            {/*</div>*/}
            {/*<input type='button' className = 'loginbtn' value='reg' onClick = {registerHandler} disabled={loading}/>*/}
            <div className = 'students'>
                {
                    students.map((student, index) => {
                        if (shown.includes(index)){
                            return (
                                <NavLink to = {'/profile/' + student.user} key = {index} className = 'studentItem soft_appearing'>
                                    <p className = 'studentName'>{student.name} </p>
                                    <p className = 'studentSurname'> {student.surname}</p>
                                    <p className = 'pic'>{String.fromCodePoint(student.pic)}</p>
                                    {/*<p className = 'testName'>{student.birthdate}</p>*/}
                                    {/*<p className = 'testName'>{student.grade}{student.letter}</p>*/}
                                </NavLink>
                            )
                        }
                })}
            </div>
        </div>
    )
}

export default Students;
