import React from "react"
import styled, { keyframes } from "styled-components"

export default function ScaleIncreasing (points, maxPoints, background)  {
    const width = points/maxPoints * 100
    const time = points/maxPoints * 3
    const animation = keyframes`
        from {  width: 0%;  }
        to { width: ${width}%; }`

    console.log(time)

    const Scale = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 60px;
    height: 70%;
    width: 0%;
    background: ${background};
    animation: ${animation} ${time}s ease-out;
    animation-fill-mode: forwards;
    `

    return <Scale/>
}
