import React, {useCallback, useContext, useEffect, useState} from 'react'
import './Appearence/Profile.css'
import './Appearence/index.css'
import {AuthContext} from "./Context/AuthContext"
import {useHttp} from "../Hooks/http.hook";
import {NavLink} from "react-router-dom";

export const Profile = () => {

  const {loading, request} = useHttp();

  const {token, userID, name, surname, username, userType} = useContext(AuthContext)
  const [results, setResults] = useState([])
  const [shown, setShown] = useState([0, 1])
  const [isSearching, activateSearching] = useState('none')
  const [search, setSearch] = useState('')

  const fetchResults = useCallback(async () => {
    try {
      const fetched = await request(`/results/${username}`, 'GET', null, {
        Authorization: `Bearer ${token}`
      })
      setResults(fetched)
    }
    catch (e) {}
  }, [token, userID])


  const handleSearchActivating = e => {
    e.preventDefault()
    switch (isSearching){
      case 'active':
        activateSearching('inactive');
        break
      case 'none':
      case 'inactive':
        activateSearching('active');
        break
    }
  }

  const setSearchFilter = e => {
    e.preventDefault()
    let search = e.target.value.toString().toLowerCase()
    setSearch(search)
    if (search != '')
      doSearch()
    else showAll()

  }

  const doSearch = e => {
    try {
      if (search != '') {
        let resultsArray = []
        results.map(result => {
          let res = ''
          result.result.map(r => {
            res += r.scaleResult.toString()
          })
          resultsArray.push((result.test + res).toString().toLowerCase())
        })

        let indexes = []
        resultsArray.map((result, i) => {
          if (resultsArray[i].toString().includes(search))
            indexes.push(i)
        })

        if (indexes.length != 0)
          setShown([...indexes])
        else showAll()
      }
      else showAll()
    }
    catch (e) {}
  }

  const showAll = () => {
    let shownSize = []
    for (let i = 0; i < results.length; i++){
      shownSize.push(i)
    }
   setShown([...shownSize])
  }

  useEffect(() => {
    document.body.style = 'transition-duration: .7s; transition-timing-function: ease-in-out; background: white;'
    fetchResults()
  })

    return (
      <div>
        <div className = 'profileContainer'>
          <div className = 'profilePic'> üë©üèª </div>
          <div className = 'profileHeader'>
            <h1>{name} {surname} </h1>
            <h2>@{username}</h2>

            {userType === 'a' &&
              <h2>–ê–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä</h2>}
            {userType === 'p' &&
              <h2>–ü–µ–¥–∞–≥–æ–≥-–ø—Å–∏—Ö–æ–ª–æ–≥</h2>}
            {userType === 'u' &&
              <h2>–£—á–µ–Ω–∏–∫</h2>}

          </div>
        </div>

        <div className = 'searchAndSort'>

            <input type = 'text' id = 'search'
                   className = {  isSearching === 'active' && 'searchAppearing' ||
                                  isSearching === 'inactive' && 'searchDisappearing' ||
                                  isSearching === 'none' && 'invisible'}
                   placeholder = '–ü–æ–∏—Å–∫ –ø–æ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞–º' onChange = {setSearchFilter}/>

          <svg className = 'searchIcon' width="250px" height="277px" viewBox="0 0 250 277" version="1.1" onClick = {handleSearchActivating}>
            <g id="Logo" stroke="none" stroke-width="1" fill="none" fill-rule="evenodd">
              {/*<path d="M256.23536,245.633373 C256.240323,245.854751 256.242819,246.076789 256.242819,246.299457 C256.242819,261.211145 245.049938,273.299457 231.242819,273.299457 C217.4357,273.299457 206.242819,261.211145 206.242819,246.299457 C206.242819,246.076789 206.245315,245.854751 206.250278,245.633373 Z" id="Combined-Shape" stroke="#4A4A4A" fill="#4A4A4A" transform="translate(231.242819, 259.466415) rotate(-40.000000) translate(-231.242819, -259.466415) "></path>*/}
              {/*<rect id="Rectangle" stroke="#4A4A4A" fill="#4A4A4A" transform="translate(199.856726, 222.096267) rotate(-40.000000) translate(-199.856726, -222.096267) " x="174.856726" y="186.596267" width="50" height="71"></rect>*/}
              {/*<circle id="Oval" stroke="#4A4A4A" stroke-width="50" cx="125" cy="125" r="100"></circle>*/}
              <path className = {   isSearching === 'active' && 'activeStroke activeFill' ||
                                    isSearching === 'inactive' && 'inactiveStroke inactiveFill' ||
                                    isSearching === 'none' && 'inactiveStroke inactiveFill'}
                   d="M256.23536,245.633373 C256.240323,245.854751 256.242819,246.076789 256.242819,246.299457 C256.242819,261.211145 245.049938,273.299457 231.242819,273.299457 C217.4357,273.299457 206.242819,261.211145 206.242819,246.299457 C206.242819,246.076789 206.245315,245.854751 206.250278,245.633373 Z" id="Combined-Shape" transform="translate(231.242819, 259.466415) rotate(-40.000000) translate(-231.242819, -259.466415) "></path>
              <rect id="Rectangle"
                    className = {   isSearching === 'active' && 'activeStroke activeFill' ||
                                    isSearching === 'inactive' && 'inactiveStroke inactiveFill' ||
                                    isSearching === 'none' && 'inactiveStroke inactiveFill'}
                    transform="translate(199.856726, 222.096267) rotate(-40.000000) translate(-199.856726, -222.096267) " x="174.856726" y="186.596267" width="50" height="71"></rect>
              <circle id="Oval"
                      className = {   isSearching === 'active' && 'activeStroke' ||
                                      isSearching === 'inactive' && 'inactiveStroke' ||
                                      isSearching === 'none' && 'inactiveStroke'}
                      stroke-width="50" cx="125" cy="125" r="100"></circle>
            </g>
          </svg>



          <svg className = 'sortIcon' width="380px" height="282px" viewBox="0 0 380 282" version="1.1">
