import React, {Component} from "react"
import './Appearence/index.css'
import styled from "styled-components"

export const Footer = () => {

    const footerY = window.innerHeight

    const Footer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    position: absolute;
    width: 100%;
    height: 100px;
    top: ${footerY}px;
    background: #FFFFFF;
    box-shadow: inset 4px 4px 5px 0px rgba(181, 181, 181, 0.2),
    inset -4px 4px 20px 0px rgba(255, 255, 255, 0.3),
     -3px -3px 5px 0px rgba(181, 181, 181, 0.5),
     3px 3px 10px 0px rgba(255, 255, 255, 0.5);
    `
    const P = styled.p`
    font-size: 14pt;
    font-family: Gill Sans, Gill Sans MT, Calibri, sans-serif;
    font-weight: normal;
    color: #4A4A4A;
    `

    return (
        <Footer>
            <P>2020 © АК </P>
            <P>Остались вопросы? a.krylova.off@gmail.com</P>
        </Footer>
    )
}

export default Footer
