import React, {useContext, useEffect, useState} from 'react';
import './Appearence/App.css';
import './Appearence/Auth.css';
import './Appearence/index.css';
import {useHttp} from "../Hooks/Http.hook";
import {AuthContext} from "./Context/AuthContext";
import {ReactComponent as INTRO} from './Appearence/Images/INTRO.svg'
import {notify} from "./Helpers/Notify";


export const Auth = () => {

    const auth = useContext(AuthContext)
    const {loading, request, error, clearError} = useHttp();
    const [form, setForm] = useState({ login: '', password: '' })

     const changeHandler = event => {
         setForm({...form, [event.target.name]: event.target.value})
     }

    const keyPressedHandler = async (e) => {
        if (form.login && form.password){
            if (e.which == 13 || e.keyCode == 13)
                await loginHandler()
        }
    }

    const loginHandler = async () => {
        try {
            const data = await request('/api/auth/login', 'POST', {...form})
            console.log(data.message)
            auth.login(data.token, data.userID, data.userType, data.name, data.surname, data.username)
        }
        catch (e) {}
    }

    useEffect(() => {
        document.title = 'Intro'
        document.body.style = 'background: #E0E7B4;'
    }, [error, clearError])


      return (
          <p className = 'authPage'>
              <INTRO id = 'intropic'/>
              <div className = 'cover' />
              <div className = 'authFormAndButton'>
                  <div className = 'authform'>
                      <input type='text' id = 'login' name='login' placeholder='Логин' onChange={changeHandler}
                             value = {form.login}
                             onKeyPress={keyPressedHandler}/>
                      <input type='password' id = 'password' name='password' placeholder='Пароль' onChange={changeHandler}
                             value = {form.password}
                             onKeyPress={keyPressedHandler}/>
                  </div>
                  <input type='button' className = 'loginbtn' onClick = {loginHandler} disabled={loading}/>
                  <div className = 'loginbtn' onClick = {loginHandler} disabled={loading}/>
              </div>
          </p>
      )
}

export default Auth
