import React, {useContext, useEffect} from "react"
import {useParams} from "react-router-dom";
import './Appearence/index.css'
import {useHttp} from "../Hooks/http.hook"
import {useAuth} from "../Hooks/auth.hook"
import {AuthContext} from "./Context/AuthContext"

export const ResultPage = () => {

    const login = useParams().id
    const link = useParams().link

    const {request} = useHttp()
    const {token} = useContext(AuthContext)

    const fetchResult = async () => {
        // const fetched = await request(`/api/tests/${link}/${login}`, 'GET', null, {
        //     Authorization: `Bearer ${token}`
        // })
        // console.log(fetched.message)
        let a = [
            [0, 1, 2],
            [5, 4, 6],
            [6, 3, 9]
        ]
        let b = [
            [0, 1, 4],
            [5, 4, 6],
            [6, 3, 9]
        ]
        alert(arrayComparing(a, b))
    }

    const handleFetchingResults = async (e) => {
        e.preventDefault()
        await fetchResult()
    }

    const arrayComparing = (arr1, arr2) => {
        let maxLength, matches

        if (arr1.length > arr2.length)
            maxLength = arr1.length
        else maxLength = arr2.length

        for (let i = 0; i < maxLength; i++) {
            let miniArr1 = arr1[i],
                miniArr2 = arr2[i]
            matches += miniArr1.filter(a => miniArr2.includes(a))
        }
        return matches
    }

    return(
        <div>
            <p>Имя теста</p>
            <p>Человек</p>
            <input type = 'button' onClick = {e => handleFetchingResults(e)}/>
        </div>
    )
}

export default ResultPage
