import React, {useContext, useEffect, useState} from "react"
import {useParams} from "react-router-dom";
import './Appearence/index.css'
import {useHttp} from "../Hooks/http.hook"
import {useAuth} from "../Hooks/auth.hook"
import {AuthContext} from "./Context/AuthContext"

export const ResultPage = () => {

    const login = useParams().id
    const link = useParams().link

    const {request} = useHttp()
    const {token} = useContext(AuthContext)

    const [result, setResult] = useState([])
    const [test, setTest] = useState('')

    const fetchResult = async () => {
        const fetched = await request(`/api/tests/${link}/${login}`, 'GET', null, {
            Authorization: `Bearer ${token}`
        })
        setTest(fetched.test)
        setResult(fetched.scales)
        console.log(result)
    }

    useEffect(() => {
        if (!test)
            fetchResult()
    }, [test])

    return(
        <div>
            <h1>{test}</h1>
            {
                // result[0].important &&
                <h2>Завышенные показатели</h2>
            }
            {
                result.map((res, i) => {
                    if (res.important)
                    return (
                        <div>
                            <p>{res.intervalName}</p>
                            <p>Ваши баллы: {res.points} / {res.maxPoints}</p>
                            <p>{res.intervalDescription}</p>
                        </div>
                    )
                })
            }

            {
                // !result[result.length - 1].important &&
                <h2>Норма</h2>
            }
            {
                result.map((res, i) => {
                    if (!res.important)
                        return (
                            <div>
                                <p>{res.intervalName}</p>
                                <p>Ваши баллы: {res.points} / {res.maxPoints}</p>
                                <p>{res.intervalDescription}</p>
                            </div>
                        )
                })
            }
        </div>
    )
}

export default ResultPage
