import React, {useContext, useEffect} from 'react'
import './Appearence/Profile.css'
import pic from '../Images/ProfilePic.png'
import './Appearence/index.css'
import NavigationBar from "./NavigationBar";
import {AuthContext} from "./Context/AuthContext"
import {useAuth} from "../Hooks/auth.hook";

export const Profile = () => {

  const {name, surname, username, userType} = useContext(AuthContext)

  useEffect(() => {
    document.body.style = 'transition-duration: .7s; transition-timing-function: ease-in-out; background: white;'
  })

    return (
      <div>
        <div className = 'profileContainer'>
          <div className = 'avatar'>
            {/*<img src = {pic} className = 'profilePic'/>*/}
            {/*<img src = {pic} className = 'profilePic'/>*/}
            <p  className = 'profilePic'>&#128105; &#8205; &#127908;</p>
          </div>
          <div className = 'profileHeader'>
            <h1>{name} {surname} </h1>
            <h2>@{username}</h2>

            {userType === 'a' &&
              <h2>Администратор</h2>}
            {userType === 'p' &&
              <h2>Педагог-психолог</h2>}
            {userType === 'u' &&
              <h2>Ученик</h2>}

          </div>

          <div className = 'profileItems'>
          </div>
        </div>
        {/*<ComponentsPanel/>*/}
      </div>
    )


}

export default Profile;
