import React, {useCallback, useContext, useEffect, useState} from 'react'
import './Appearence/Profile.css'
import './Appearence/index.css'
import {AuthContext} from "./Context/AuthContext"
import {useHttp} from "../Hooks/http.hook";
import {NavLink} from "react-router-dom";

export const Profile = () => {

  const {loading, request} = useHttp();

  const {token, userID, name, surname, username, userType} = useContext(AuthContext)
  const [results, setResults] = useState([])
  const [shown, setShown] = useState('')
  const [search, setSearch] = useState()

  const fetchResults = useCallback(async () => {
    try {
      const fetched = await request(`/results/${username}`, 'GET', null, {
        Authorization: `Bearer ${token}`
      })
      setResults(fetched)
    }
    catch (e) {}
  }, [token, userID])

  const doSearch = e => {
    e.preventDefault()

    let resultsArray = []
    results.map((result, i) => {
      let test = result.test
      let res = ''
      result.result.map(r => {
        res += r.scaleResult.toString()
      })
      resultsArray.push((test + res).toString().toLowerCase())

    })

    resultsArray.map((result, i) => {
      if (resultsArray[i].toString().includes(search))

        // console.log("i is ", i, " result is ", result)
        setShown(i)
        console.log("shown is ", shown)
    })

    //
    //   alert("search: " + search + ", array: " + resultsArray)

  }

  const setSearchFilter = e => {
    e.preventDefault()
    let search = e.target.value.toString().toLowerCase()
    setSearch(search)
  }


  useEffect(() => {
    document.body.style = 'transition-duration: .7s; transition-timing-function: ease-in-out; background: white;'
    fetchResults()
  })

    return (
      <div>
        <div className = 'profileContainer'>
          <div className = 'profilePic'> 👩🏻 </div>
          <div className = 'profileHeader'>
            <h1>{name} {surname} </h1>
            <h2>@{username}</h2>

            {userType === 'a' &&
              <h2>Администратор</h2>}
            {userType === 'p' &&
              <h2>Педагог-психолог</h2>}
            {userType === 'u' &&
              <h2>Ученик</h2>}

          </div>
        </div>

        <input type = 'text' placeholder = 'Search' onChange = {setSearchFilter}/>
        <input type = 'button' value = 'Сортировать' onClick = {doSearch}/>

        <div id = 'resultsCollection' className = 'results'>
          {
            results.map((result, index) => {
            return (
                <NavLink to = {'/'} key = {index} className = 'resultItem soft_appearing'>
                  <p className = 'resultTestName'>{result.test}</p>
                  <div className = 'shortResult'>
                  {
                    result.result.map((result, i) => {
                      return(
                          <p key = {i}>{result.scaleResult}</p>
                      )
                    }
                  )}
                  </div>
                </NavLink>
            )
           })
          }
        </div>

      </div>
    )


}

export default Profile;
