import React, {useCallback, useContext, useEffect, useState} from 'react'
import './Appearence/index.css'
import './Appearence/TestPage.css'
import {AuthContext} from "./Context/AuthContext";
import {useHttp} from "../Hooks/http.hook";
import {useParams} from 'react-router-dom'

export const TestPage = () => {

    const [test, setTest] = useState({ name: '', description: '', shortDescription: ''})
    const [questions, setQuestions] = useState()
    const {token} = useContext(AuthContext)
    const {loading, request, error, clearError} = useHttp();
    const link = useParams().id

    const fetchTestData = useCallback(async () => {
        try {
            const fetched = await request(`/api/tests/${link}`, 'GET', null, {
                Authorization: `Bearer ${token}`
            })
            setTest({name: fetched[0].name, description: fetched[0].description, shortDescription: fetched[0].shortDescription})
        }
        catch (e) {}
    }, [token, request])

    useEffect(() => {
        fetchTestData()
    }, [fetchTestData])



    return (
        <div>
            <h1 cls>{test.name}</h1>
            <p>{test.shortDescription}</p>
            <p>{test.description}</p>
        </div>
    )
}
