import React from 'react';
import Auth from './components/Auth'
import {BrowserRouter as Router} from 'react-router-dom'
import './components/Appearence/index.css';
import {useRouts} from "./components/Routes";
import {useAuth} from "./hooks/auth.hook";
import {AuthContext} from "./components/context/AuthContext";

function App() {
    const {token, userID, userType, login, logout, ready} = useAuth()
    const isAuthenticated = !!token
    const routes = useRouts(isAuthenticated, userType)

    if (!ready) {
        return <div>
            <h1>loading...</h1>
        </div>
    }

  return (
      <AuthContext.Provider value={ {token, userID, userType, login, logout, isAuthenticated } }>
          <Router>
            <div>
                {routes}
            </div>
          </Router>
      </AuthContext.Provider>
  );
}

export default App;
