import React from "react";
import {NavLink} from "react-router-dom";
import './Appearence/index.css'
import {useHistory, useLocation} from "react-router-dom";


export const NavigationBar = () => {

    const location = useLocation()
    const currentLocation = location.pathname.slice(1)
    const history = useHistory()

    // const elements = document.getElementById('hey').childNodes
    // elements.forEach(element => makeSmaller(element))


    const handleClick = (e) => {
        // e.preventDefault()
        history.push(`/${e.target.id}`)
        makeBigger(document.getElementById(currentLocation))
    }
    const makeSmaller = (element) => {
        element.style.height = '80px'
        element.style.width = '80px'
    }
    const makeBigger = (element) => {
        element.style.height = '100px'
        element.style.width = '100px'
    }

    return (
        <div id = 'hey'>
            <div className='protocircle' id = 'profile' onClick = {handleClick} />
            <div className='protocircle' id = 'students' onClick = {handleClick}/>
            <div className='protocircle' id = 'tests' onClick = {handleClick}/>
        </div>
    )
}
    export default NavigationBar
