import React, {useContext} from 'react';
import {Switch, Route, Redirect} from 'react-router-dom'
import Profile from "./Profile";
import Auth from "./Auth";
import Header from "./Header";
import NavigationBar from "./NavigationBar";
import Students from "./Students";
import Tests from "./Tests";
import TestPage from "./TestPage";
import {AuthContext} from "./Context/AuthContext";
import AddTest from "./AddTest";

import './Appearence/index.css'
import './Appearence/Auth.css'
import ProfileViewForStudents from "./ProfileViewForStudents";

// from App.js (Client)
export const useRouts = (isAuthenticated, type, username) => {

    const auth = useContext(AuthContext)
    if (isAuthenticated){
        if (type === 'p'){
            return (
                <Switch>
                    <Route path = '/profile/:id' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                                <Profile/>
                            </div>
                        </div>
                    </Route>

                    <Route path = '/students' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                                <Students/>
                            </div>
                        </div>
                    </Route>

                    <Route path = '/tests' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                                <Tests/>
                            </div>
                        </div>
                    </Route>

                    <Route path = '/tests/:id' className = 'greenPage' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                                <TestPage/>
                            </div>
                        </div>
                    </Route>
                    <Route path = '/addtest' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                                <AddTest/>
                            </div>
                        </div>
                    </Route>


                    <Route path = '/tests/:link/edit' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                                <AddTest/>
                            </div>
                        </div>
                    </Route>

                    <Redirect to = '/tests'/>
                </Switch>
            )
        }
        // else if (type === 'a') {
        //     return (
        //     <Switch>
        //         <Route path = '/profile/:id' exact>
        //             <div className = 'page'>
        //                 <Header/>
        //                 <NavigationBar/>
        //                 <div className='pageContent'>
        //                     <Profile/>
        //                 </div>
        //             </div>
        //         </Route>
        //
        //         <Route path = '/students' exact>
        //             <div className = 'page'>
        //                 <Header/>
        //                 <NavigationBar/>
        //                 <div className='pageContent'>
        //                     <Students/>
        //                 </div>
        //             </div>
        //         </Route>
        //
        //         <Route path = '/tests' exact>
        //             <div className = 'page'>
        //                 <Header/>
        //                 <NavigationBar/>
        //                 <div className='pageContent'>
        //                     <Tests/>
        //                 </div>
        //             </div>
        //         </Route>
        //
        //         <Route path = '/tests/:id' className = 'greenPage'>
        //             <div className = 'page'>
        //                 <Header/>
        //                 <NavigationBar/>
        //                 <div className='pageContent'>
        //                     <TestPage/>
        //                 </div>
        //             </div>
        //         </Route>
        //
        //         <Redirect to = '/tests'/>
        //     </Switch>
        //     )
        // }
        else if (type === 's') {
            return (
                <Switch>
                    <Route path = '/profile' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                                <ProfileViewForStudents/>
                            </div>
                        </div>
                    </Route>
                    <Route path = '/tests' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                                <Tests/>
                            </div>
                        </div>
                    </Route>
                    <Route path = '/tests/:id' className = 'greenPage' exact>
                        <div className = 'page'>
                            <Header/>
                            <NavigationBar/>
                            <div className='pageContent'>
                                <TestPage/>
                            </div>
                        </div>
                    </Route>

                    <Redirect to = '/tests'/>
                </Switch>
            )
        }
    }
    else {
        return (
            <Switch>
                <Route path = '/' exact>
                    <div className = 'authPageGr'>
                        <Auth/>
                    </div>
                </Route>

                <Redirect to = '/'/>
            </Switch>
        )
    }
}
