import React from "react"
import styled, { keyframes } from "styled-components"

export default function ScaleIncreasing (points, maxPoints, background, color)  {
    const width = points / maxPoints * 100
    const time = width * 3.5 / maxPoints
    const increasing = keyframes`
        from {  width: 0%;  }
        to { width: ${width}%; }`
    const textColoring = keyframes`
        from {  color: ${background}  }
        to { color: ${color}%; }`


    const Scale = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 50px;
    height: 100%;
    width: 0%;
    background: ${background};
    animation: ${increasing} ${time}s ease-out;
    animation-fill-mode: forwards;
    `

    const Points = styled.p`
    word-spacing: 5px;
    min-width: 100px;
    font-family: Gill Sans, Gill Sans MT, Calibri, sans-serif;
    font-size: 13pt;
    color: ${color};
    animation: ${textColoring} ${time}s ease-out;
    animation-delay: 2s;
    animation-fill-mode: forwards;
    `

    return <Scale>
        <Points>{points} из {maxPoints}</Points>
        </Scale>
}

