import React, {useCallback, useContext, useEffect, useState} from "react";
import {NavLink} from "react-router-dom";
import './Appearence/index.css'
import {useHistory, useLocation} from "react-router-dom";
import {AuthContext} from "./Context/AuthContext";
import {useHttp} from "../Hooks/http.hook";


export const NavigationBar = () => {

    const location = useLocation()
    const {userType, username, token} = useContext(AuthContext)
    const {request} = useHttp();
    const currentLocation = location.pathname.slice(1)
    const [pic, setPic] = useState([])

    const fetchPic = useCallback(async () => {
        try {
            const fetched = await request(`/users/get/${username}`, 'GET', null, {
                Authorization: `Bearer ${token}`
            })
            setPic(fetched.pic)
            console.log(pic)
        }
        catch (e) {}
    }, [token, username])

    useEffect(() => {
        const small = 'scale(0.8)'
        const big = 'scale(1.2)'
        try {
            fetchPic()
            // if (userType === 's') document.getElementById('profile').style.transform = small
            if (userType === 'p') document.getElementById('profile/' + username).style.transform = small
            // document.getElementById('profile').style.transform = small
            document.getElementById('students').style.transform = small
            document.getElementById('tests').style.transform = small
            document.getElementById(currentLocation).style.transform = big
        }
        catch (e) {}
    }, [currentLocation])

    const showNavBar = () => {
        // console.log(document.getElementsByClassName('navBar').style)
        // document.getElementsByClassName('navBar').style.left = '1%'
    }

    const hideNavBar = () => {
        // document.getElementsByClassName('navBar').style.left = '-3%'
    }

    return (
        <div>

            {/*<div className = 'navBarCover' onMouseEnter={showNavBar} onMouseLeave={hideNavBar}/>*/}
            <div className = 'navBar'>
                <NavLink to = '/tests' className = 'protocircle' id = 'tests'/>
                {
                    userType === 'p' &&
                    <NavLink to = {'/profile/' + username} className = 'protocircle' id = {'profile/' + username} />
                }
                {
                    userType === 's' &&
                    <NavLink to = '/profile' className = 'protocircle' id = 'profile' />
                }
                {
                    userType === 'p' &&
                    <NavLink to = '/students' className='protocircle' id = 'students'/>
                }
            </div>
        </div>
    )
}
    export default NavigationBar
