import React, {useCallback, useContext, useEffect, useState} from "react";
import {NavLink} from "react-router-dom";
import './Appearence/index.css'
import {useHistory, useLocation} from "react-router-dom";
import {AuthContext} from "./Context/AuthContext";
import {useHttp} from "../Hooks/http.hook";
import {emojiToString} from "./Functions and const/Emoji";


export const NavigationBar = () => {

    const location = useLocation()
    const {userType, username, token} = useContext(AuthContext)
    const {request} = useHttp();
    const currentLocation = location.pathname.slice(1)
    const [pic, setPic] = useState([])
    const [navBarClasses, setNavBarClasses] = useState('navBar')


    const fetchPic = useCallback(async () => {
        try {
            const fetched = await request(`/users/get/${username}`, 'GET', null, {
                Authorization: `Bearer ${token}`
            })
            setPic(fetched.pic)
        }
        catch (e) {}
    }, [token, username])

    useEffect(() => {
        const small = 'transform: scale(0.8); opacity: 0.3;'
        const big = 'transform: scale(1.2); opacity: 1;'
        try {
            fetchPic()
            if (userType === 'p') document.getElementById('profile/' + username).style = small
            document.getElementById('students').style = small
            document.getElementById('tests').style = small
            document.getElementById(currentLocation).style = big
        }
        catch (e) {}
    }, [currentLocation])

    const showNavBar = e => {
        e.preventDefault()
        setNavBarClasses('navBar')
        console.log(navBarClasses)
    }

    const hideNavBar = e => {
        e.preventDefault()
        setNavBarClasses('hiddenNavBar')
        console.log(navBarClasses)
    }

    return (
        <div>

            <div className = 'navBarCover' onMouseEnter={e => showNavBar(e)} onMouseLeave={e => hideNavBar(e)}/>

            <div className = {navBarClasses} id = 'navBar'>
                <NavLink to = '/tests' className = 'picNavBar' id = 'tests'>
                    📋
                </NavLink>
                {
                    userType === 'p' &&
                    <NavLink to = {'/profile/' + username} className = 'picNavBar' id = {'profile/' + username}>
                        {emojiToString(pic)}
                        { !pic[0] && '😊️'}
                    </NavLink>
                }
                {
                    userType === 's' &&
                    <NavLink to = '/profile' className = 'picNavBar' id = 'profile'>
                        {emojiToString(pic)}
                        { !pic[0] && '😊️'}
                    </NavLink>
                }
                {
                    userType === 'p' &&
                    <NavLink to = '/students' className='picNavBar' id = 'students'>
                        👩🏻‍🏫
                        <p></p>
                    </NavLink>
                }
            </div>
        </div>
    )
}
    export default NavigationBar
