import React from "react"
import styled, { keyframes } from "styled-components"

export default function ConfirmWindow ()  {

    const appearing = keyframes`
        from {  width: 0%;  }
        to { width: 100%; }`

    const Window = styled.div`
    display: block;
    position: fixed;
    left: 45%;
    top: 45%;
    border-radius: 50px;
    height: 300px;
    width: 300px;
    background: black;
    animation: ${appearing} 1s ease-out;
    animation-fill-mode: forwards;
    `

    const a = React.createElement(Window, null)

    return a
}
