import React, {useContext, useEffect, useState} from 'react';
import './Appearence/App.css';
import './Appearence/Auth.css';
import {useAuth as auth} from "../Hooks/auth.hook";
import {useHttp} from "../Hooks/http.hook";
import {useMessage} from "../Hooks/message.hook";
import {AuthContext} from "./Context/AuthContext";
import {ReactComponent as INTRO} from './Appearence/Images/INTRO.svg'


export const Auth = () => {

    // !!!!!!!!!!!!!!!!!!!
    // const auth = useContext(AuthContext)
    const {loading, request, error, clearError} = useHttp();
    const [form, setForm] = useState({ login: '', password: '' })
    const message = useMessage()

    useEffect(() => {
        message(error)
        clearError()
        document.body.style = 'background: white;'
        // document.body.style = 'background: #E0E7B4;'
    }, [error, message, clearError])

     const changeHandler = event => {
         setForm({...form, [event.target.name]: event.target.value})
     }

    const keyPressedHandler = e => {
        if (form.login && form.password){
            if (e.which == 13 || e.keyCode == 13)
                loginHandler()
        }
    }

    const loginHandler = async () => {
        try {
            const data = await request('auth/login', 'POST', {...form})
            console.log(data.message)
            auth.login(data.token, data.userID, data.userType, data.name, data.surname, data.username)

            // OPTIONAL
            // console.log('USER TYPE: ', data.userType)
            // console.log('USER NAME: ', data.name)
            // console.log('USER SURNAME: ', data.surname)
        }
        catch (e) {}
    }


      return (
          <div className = 'authPage'>
              <INTRO id = 'intropic'/>
              <div className = 'authFormAndButton'>
                  <div className = 'authform'>
                      <input type='text' id = 'login' name='login' placeholder='Логин' onChange={changeHandler} value = {form.login} onKeyPress={keyPressedHandler}/>
                      <input type='password' id = 'password' name='password' placeholder='Пароль' onChange={changeHandler} value = {form.password} onKeyPress={keyPressedHandler}/>
                  </div>
                  <input type='button' className = 'loginbtn' onClick = {loginHandler} disabled={loading}/>
              </div>
          </div>
      )
}

export default Auth
