import React from "react"
import { keyframes, css } from "styled-components"

export const ScaleIncreasing = (points, maxPoints, background) => {
    const width = points/maxPoints * 100
    const animation = keyframes`
        from {  width: 0%;  }
        to { width: ${width}%; }`

    const style = css`
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 60px;
    height: 70%;
    width: 0%;
    background: ${background};
    animation: ${animation} 2s ease-out;
    animation-fill-mode: forwards;
    `

    return <p style = {style}/>
}

export default ScaleIncreasing
