import React, {useContext, useEffect, useState} from "react"
import {useParams} from "react-router-dom";
import './Appearence/index.css'
import {useHttp} from "../Hooks/http.hook"
import {useAuth} from "../Hooks/auth.hook"
import {AuthContext} from "./Context/AuthContext"

export const ResultPage = () => {

    const login = useParams().id
    const link = useParams().link

    const {request} = useHttp()
    const {token} = useContext(AuthContext)

    const [result, setResult] = useState([])
    const [test, setTest] = useState('')
    const [error, setError] = useState('Пока что такого результата нет')

    const fetchResult = async () => {
        try {
            const fetched = await request(`/api/tests/${link}/${login}`, 'GET', null, {
                Authorization: `Bearer ${token}`
            })
            if (!fetched.message) {
                setError(null)
                setTest(fetched.test)
                setResult(fetched.scales)
            }
        }
        catch (e) {
            console.log(e)
        }
    }

    useEffect(() => {
        document.body.style = 'transition-duration: .7s; transition-timing-function: ease-in-out; background: white;'
    })
    useEffect(() => {
        if (!test)
            fetchResult()
    }, [test])

    return(
        <div>

            <p className = 'notFound'>
                {error}
            </p>

            <h1>{test}</h1>

                <h2>Завышенные показатели</h2>
            {
                result.map((res, i) => {
                    if (res.important)
                    return (
                        <div key = {i}>
                            <p>{res.name}: {res.intervalName}</p>
                            <p>Ваши баллы: {res.points} / {res.maxPoints}</p>
                            <p>{res.intervalDescription}</p>
                        </div>
                    )
                })
            }

                <h2>Норма</h2>
            {
                result.map((res, i) => {
                    if (!res.important)
                        return (
                            <div key = {i}>
                                <p>{res.name}: {res.intervalName}</p>
                                <p>Ваши баллы: {res.points} / {res.maxPoints}</p>
                                <p>{res.intervalDescription}</p>
                            </div>
                        )
                })
            }
        </div>
    )
}

export default ResultPage
