import * as cyrToTranslit from 'cyrillic-to-translit-js'
import {useContext} from "react";

const createShortLink = name => {
    const link = cyrToTranslit(name)
    console.log(name)
    return 0
}

export const Translit = useContext({
    createShortLink: createShortLink
})
