import React, {useCallback, useContext, useEffect, useState} from 'react'
import classNames from 'classnames/bind'
import './Appearence/index.css'
import './Appearence/AddTest.css'
import {AuthContext} from "./Context/AuthContext";
import {Translit} from "./Context/Translit";
import {useHttp} from "../Hooks/http.hook";
import {ReactComponent as AddIcon} from "./Appearence/Images/AddIcon.svg"
import {ReactComponent as CancelIcon} from "./Appearence/Images/CancelIcon.svg"


export const AddTest = () => {

    const {token, userID} = useContext(AuthContext)
    const {createShortLink} = useContext(Translit)
    const {loading, request, error, clearError} = useHttp();

    const [drafts, setDrafts] = useState()
    const [fromDrafts, setFromDrafts] = useState(false)

    const [testData, setTestData] = useState({
        name: '',
        link: '',
        shortDescription: '',
        description: '',
        scales: [{
            name: '' ,
            k: '',
            intervals: [{
                name: '',
                description: '',
                min: '',
                max: '',
                important: false
            }]
        }
        ],
        questions: [],
        answers: []
    })

    const isImportantBtn = isImportant => {
     return classNames({inactiveImportantBtn: !isImportant, activeImportantBtn: isImportant})
    }

    const fetchDrafts = useCallback(async() => {
        try {
            const fetched = await request('addtest/drafts', 'GET', null, {
                Authorization: `Bearer ${token}`
            })
            setDrafts(fetched)
        }
        catch (e) {}
    }, [token, request])

    const handleSaving = async(e) => {
        e.preventDefault()
        await saveTestDraft()
    }

    const saveTestDraft = async() => {
        const data = await request('addtest/savedraft', 'POST', {...testData, date: new Date()})
        // console.log(data.message)
        if (data.message === 'Draft is saved') alert(data.message)
    }


    // const handleSelect = e => {
    //     e.preventDefault()
    //     setFromDrafts(!fromDrafts)
    // }

    const chooseDraft = e => {
        e.preventDefault()
        setTestData(drafts[e.target.value])
    }

    const changeHandler = e => {
        e.preventDefault()
        if (e.target.name === 'name') {
            let link = createShortLink(e.target.value)
            setTestData({...testData, [e.target.name]: e.target.value, link})
        }
        else setTestData({...testData, [e.target.name]: e.target.value})
    }

    const handleAddingScale = e => {
        e.preventDefault()
        const scales = testData.scales
        scales.push({
            name: '' ,
            k: '',
            intervals: [{
                name: '',
                description: '',
                min: '',
                max: '',
                important: false
            }]
        })
        setTestData({...testData, scales})
    }

    const handleDeletingScale = (e, i) => {
        e.preventDefault()
        let scales = testData.scales
        scales.splice(i, 1)
        setTestData({...testData, scales})
        if (scales.length == 0) handleAddingScale(e)
    }

    const handleAddingInterval = (e, i) => {
        e.preventDefault()
        let scales = testData.scales
        let intervals = scales[i]['intervals']
        intervals.push({
                name: '',
                description: '',
                min: '',
                max: '',
                important: false
        })
        scales['intervals'] = intervals
        setTestData({...testData, scales})
    }

    const handleDeletingInterval = (e, i, j) => {
        e.preventDefault()
        let scales = testData.scales
        let intervals = scales[i]['intervals']
        intervals.splice(j, 1)
        scales['intervals'] = intervals
        setTestData({...testData, scales})
        if (intervals.length == 0) handleAddingInterval(e, i)
    }

    const handleAddingQuestion = e => {
        e.preventDefault()
        const questions = testData.questions
        questions.push('')
        setTestData({...testData, questions})
    }

    const handleDeletingQuestion = (e, i) => {
        e.preventDefault()
        let questions = testData.questions
        questions.splice(i, 1)
        setTestData({...testData, questions})
        if (questions.length == 0) handleAddingQuestion(e)
    }

    const changeScaleHandler = (e, i) => {
        let scales = testData.scales
        scales[i][e.target.name] = e.target.value
        setTestData({...testData, scales})
    }

    const changeIntervalHandler = (e, i, j) => {
        e.preventDefault()
        let scales = testData.scales
        let intervals = scales[i]['intervals']

        if (e.target.id === 'isImportant')
            intervals[j]['important'] = !intervals[j]['important']

        else
            intervals[j][e.target.name] = e.target.value

        scales[i]['intervals'] = intervals
        setTestData({...testData, scales})
    }

    const colorBackground = (e, el, i, j, c) => {
        try {
            let color
            switch (c) {
                case 'red':
                    document.getElementById(el + i + j).style.background = 'linear-gradient(90deg, #ffffff 0%, #fbe7d6 100%)';
                    break;

                    case 'white':
                        document.getElementById(el + i + j).style.background = 'inherit';
                    break;
            }
        }
        catch (e) {}
    }


    useEffect(() => {
        document.body.style = 'transition-duration: 1s; transition-timing-function: ease-in-out; background: #FFFFFF;'
        fetchDrafts()
    }, [])

    useEffect(() => {
        if (fromDrafts && !drafts){
            fetchDrafts()
        }
        else if (!fromDrafts && drafts && testData.name){
            saveTestDraft()
            fetchDrafts()
            setTestData({
                name: '',
                link: '',
                shortDescription: '',
                description: '',
                scales: [{
                    name: '' ,
                    k: '',
                    intervals: [{
                        name: '',
                        description: '',
                        min: '',
                        max: '',
                        important: false
                    }]
                }
                ],
                questions: ['']
            })
        }
    }, [fromDrafts])

    useEffect(() => {
        // setInterval(() => {console.log('hi')}, 1000)
    }, [])


    return (
        // <div className = 'soft_appearing'>
        <div className = 'addingTest'>
            <h1>Добавить тест</h1>

            <select onChange = {() => setFromDrafts(!fromDrafts)}>
                <option defaultValue value = 'new'>вручную</option>
                <option value = 'draft'>из черновиков</option>
            </select>

            {
                fromDrafts && drafts &&
                 <select defaultValue = '' onChange = {e => chooseDraft(e)}>
                     <option value = ''>Выберите черновик</option>
                    {drafts.map((draft, i) => {
                        return (
                            <option key = {i} value = {i}>{draft.name}</option>
                        )
                    })}
                 </select>
            }

            <div className = 'addingTestModule'>
                <h2>Название методики</h2>
                {   testData.name !== 'Без названия' &&
                    <input type = 'text' name = 'name' value = {testData.name} placeholder = 'Название методики' onChange = {e => changeHandler(e)}/>}
                {   testData.name === 'Без названия' &&
                <input type = 'text' name = 'name' value = '' placeholder = 'Название методики' onChange = {e => changeHandler(e)}/>}



                <div className = 'shortLinkBox'>
                    <p>Краткая ссылка: </p>
                    <input type = 'text' name = 'link' value = {testData.link} placeholder = 'Краткая ссылка' onChange = {e => changeHandler(e)}/>
                </div>
            </div>

            <div className = 'addingTestModule'>
                <h2>Краткое описание</h2>
                <textarea name = 'shortDescription' value = {testData.shortDescription} placeholder = 'Краткое описание методики будет отображаться на странице со всеми методиками' onChange = {e => changeHandler(e)}/>
            </div>

            <div className = 'addingTestModule'>
                <h2>Описание</h2>
                <textarea name = 'description' value = {testData.description} placeholder = 'Полное описание методики будет отображаться на странице методики' onChange = {e => changeHandler(e)}/>
            </div>

            <div className = 'addingTestModule'>
                <h2>Шкалы</h2>
                <div className = 'addingTestModule'>
                <p>Шкальные интервалы – это бываоыаыдйуж ваьыладыыьвдfsddsdffsdsdfsfdfdfdssfdsfdsdfsdfdа ываsdjskdjflkdsjfklsdjklfsdjlfksdjлы. Вдлывалад жавдыаж</p>
                </div>

                <div className = 'scales'>
                {
                    testData.scales.map((scale, i) => {
                        return(
                            <div className = 'scaleBox' id = {'scale' + i}>
                                <CancelIcon id = 'scaleDeletingIcon' onClick = { e => handleDeletingScale(e, i) } titile = 'Убрать эту шкалу'
                                            onMouseEnter = {e => colorBackground(e, 'scale', i, '', 'red')} onMouseLeave = {e => colorBackground(e, 'scale',  i, '', 'white')}/>
                            <div className = 'scale soft_appearing' key = {i}>
                                <input type = 'text' name = 'name' placeholder = {'Название шкалы ' + (i + 1)}  value = {scale.name} onChange = { e => changeScaleHandler(e, i) }/>

                                <div className = 'addingTestModule'>
                                <p>На сколько будут умножаться баллы?</p>
                                <input type = 'number' name = 'k' placeholder = 'Коэффициент' min = '1' value = {scale.k} onChange = { e => changeScaleHandler(e, i) }/>
                                </div>

                            </div>
                                <div className = 'intervals'>
                                    {/*<h3>Шкальные интервалы</h3>*/}
                                {
                                    scale.intervals.map((interval, j) => {
                                        return (
                                            <div className = 'interval soft_appearing' id = {'interval' + i + j} key = {j}>
                                                <CancelIcon onMouseEnter = {e => colorBackground(e, 'interval', i, j, 'red')} onMouseLeave = {e => colorBackground(e, 'interval', i, j, 'white')} id = 'intervalDeletingIcon' onClick = { e => handleDeletingInterval(e, i, j) } titile = 'Убрать этот интервал'/>
                                                <input type = 'text' id = 'intervalName' name = 'name' value = {interval.name} placeholder = {'Интервал ' + (j + 1)} onChange = { e => changeIntervalHandler(e, i, j) }/>

                                                <p id = 'intervalP'>Интервал подразумевает нарушение, выход за пределы нормы?</p>
                                                <div id = 'isImportant' className = { isImportantBtn(interval.important) } onClick = { e => changeIntervalHandler(e, i, j) }>
                                                    <p onClick = { e => changeIntervalHandler(e, i, j) }>
                                                        {interval.important && 'Да'}{!interval.important && 'Нет'}
                                                    </p>
                                                </div>

                                                <textarea id = 'intervalDescription' name = 'description' value = {interval.description} placeholder = 'Комментарий' onChange = { e => changeIntervalHandler(e, i, j) }/>

                                                <p id = 'intervalP2'>Сколько баллов по шкале нужно набрать, чтобы попасть в интервал?</p>

                                                <div id = 'intervalMin'>
                                                    <p>От </p>
                                                    <input type = 'number' name = 'min' value = {interval.min} placeholder = 'баллов' onChange = { e => changeIntervalHandler(e, i, j) }/>
                                                </div>

                                                <div id = 'intervalMax'>
                                                    <p>До </p>
                                                    <input type = 'number' name = 'max' value = {interval.max} placeholder = 'баллов' onChange = { e => changeIntervalHandler(e, i, j) }/>
                                                </div>

                                            </div>)})
                                }
                                    <div id = 'intervalAddingIcon' onClick = { e => handleAddingInterval(e, i) }>
                                        <AddIcon title = 'Новый интервал'/>
                                        <p>Ещё один интервал</p>
                                    </div>
                                </div>
                            </div>)
                    })
                }
                    <div>
                        <p>На какие вопросы нужно ответить "да"?</p>
                        <div className = { isImportantBtn(false) }/>
                        <div className = { isImportantBtn(false) }/>
                        <div className = { isImportantBtn(false) }/>
                        <div className = { isImportantBtn(false) }/>
                        <div className = { isImportantBtn(false) }/>

                    </div>

                </div>

                <div id = 'scaleAddingIcon' onClick = { e => handleAddingScale(e) }>
                    <AddIcon title = 'Новая шкала'/>
                    <p>Ещё одна шкала</p>
                </div>
            </div>

            <div className = 'addingTestModule'>
            <h2>Вопросы</h2>
            </div>

            <div className = 'addingTestModule'>
                <p>Варианты ответов:</p>
            </div>

            <div className = 'addingTestModule'>
                <div className = 'answersAdd'>
                    {
                        testData.answers.map((answer, i) => {
                            return(
                                <div className = 'questionAdd' key = {i}>
                                    <input type = 'text' name = {'a' + i} id = {'a' + i} placeholder = {'Вариант ответа ' + i} value = {testData.questions[i]} onChange = { e => changeScaleHandler(e, i) }/>
                                    <CancelIcon onMouseEnter = {e => colorBackground(e, 'q', i, '', 'red')} onMouseLeave = {e => colorBackground(e, 'q', i, '', 'white')} onClick = { e => handleDeletingAnswer(e, i) } titile = 'Убрать этот вариант ответа'/>
                                </div>)
                        })
                    }
                </div>
                <AddIcon onClick = { e => handleAddingQuestion(e) }/>
            </div>

            <div className = 'addingTestModule'>
            <div className = 'questionsAdd'>
            {
                testData.questions.map((question, i) => {
                    return(
                        <div className = 'questionAdd' key = {i}>
                            <input type = 'text' name = {'q' + i} id = {'q' + i} placeholder = {'Текст вопроса ' + i} value = {testData.questions[i]} onChange = { e => changeScaleHandler(e, i) }/>
                            <CancelIcon onMouseEnter = {e => colorBackground(e, 'q', i, '', 'red')} onMouseLeave = {e => colorBackground(e, 'q', i, '', 'white')} onClick = { e => handleDeletingQuestion(e, i) } titile = 'Убрать этот вопрос'/>
                        </div>)
                })
            }
            </div>
            <AddIcon onClick = { e => handleAddingQuestion(e) }/>
            </div>

            <div className = 'registerbtn' value = 'Ок' onClick = {e => handleSaving(e)}></div>


        </div>


    )
}

export default AddTest;
