import React, {useCallback, useContext, useEffect, useState} from 'react'
import './Appearence/index.css'
import './Appearence/TestPage.css'
import {AuthContext} from "./Context/AuthContext";
import {useHttp} from "../Hooks/http.hook";
import {useHistory, useParams} from 'react-router-dom'
import {NavLink} from "react-router-dom";

export const TestPage = () => {

    const link = useParams().id
    const history = useHistory()

    const [test, setTest] = useState({ name: '', description: '', shortDescription: '', questions: [], answers: []})
    const {token, userID, username} = useContext(AuthContext)
    const [answers, setAnswers] = useState([])
    const {loading, request} = useHttp()


    const [content, setContent] = useState('description')
    const [index, setIndex] = useState(0)
    const [goNext, setGoNext] = useState(false)

    const fetchTestData = useCallback(async () => {
        try {
            const fetched = await request(`/api/tests/${link}`, 'GET', null, {
                Authorization: `Bearer ${token}`
            })
            setTest(fetched)
        }
        catch (e) {}
    }, [token, request])

    const saveAnswers = async (e) => {
        e.preventDefault()
        console.log(answers)
        try {
            const data = await request(`${link}/save`, 'POST', {userID, answers, link, date: new Date()})
            console.log(data.message)
            if (data.message === 'Result is saved')
                history.push(`/tests/${link}/${username}`)
        }
        catch (e) {}
    }

    const showTest = e => {
        e.preventDefault()
        setContent('testStartPage')
    }

    // const yesHandler = e => {
    //     e.preventDefault()
    //     document.getElementById('y' + index).style = 'transform: scale(1.2)'
    //     document.getElementById('n' + index).style = 'transform: scale(1)'
    //     setAnswers({...answers, ["a" + e.target.id]: true })
    //     setGoNext(true)
    // }
    //
    // const noHandler = e => {
    //     e.preventDefault()
    //     document.getElementById('y' + index).style = 'transform: scale(1)'
    //     document.getElementById('n' + index).style = 'transform: scale(1.2)'
    //     setAnswers({...answers, ["a" + e.target.id]: false })
    //     setGoNext(true)
    // }
    //
    const next = e => {
        e.preventDefault()
    //     if (goNext){
    //         if (index < test.questions)setIndex(index + 1)
    //         document.getElementById('y' + index).style = 'transform: scale(1)'
    //         document.getElementById('n' + index).style = 'transform: scale(1)'
    //         try { document.getElementById('next' + index).style = 'transform: scale(1.2)' } catch (e) {}
    //         try { document.getElementById('next' + index).style = 'transform: scale(1)' } catch (e) {}
    //         try { document.getElementById('back' + index).style = 'transform: scale(1)' } catch (e) {}
    //         setGoNext(false)
    //     }
    }

    const back = e => {
        e.preventDefault()
    //     if (index > 0) setIndex(index - 1)
    //     document.getElementById('y' + index).style = 'transform: scale(1)'
    //     document.getElementById('n' + index).style = 'transform: scale(1)'
    //     try { document.getElementById('next' + index).style = 'transform: scale(1)' } catch (e) {}
    //     try { document.getElementById('back' + index).style = 'transform: scale(1.2)' } catch (e) {}
    //     setGoNext(false)
    }

    const handleAnswering = (e, i, j) => {
        let answ = answers

        if (!answ[j])
            answ[j] = []

        if (!answ[j].includes(i))
            answ[j].push(i)

        for (let k = 0; k < answ.length; k++){
            if (!answ[k])
                answ[k] = []
            else {
                if (answ[k].includes(i)){
                    let index = answ[k].indexOf(i)
                    if (index != -1 && j != k)
                        answ[k].splice(index, 1)
                }
                answ[k].sort((a, b) => a > b ? 1 : -1)
            }
        }

        setAnswers(answ)
    }

    useEffect(() => {
        fetchTestData()
        if (content === 'testStartPage')
            document.body.style = 'transition-duration: 1s; transition-timing-function: ease-in-out; background: #E6EBC3;'
    }, [fetchTestData, content])


    return (
        <div>
            { content === 'description' &&
                <div className = 'testInfo'>
                    <h1 className = 'bigTestName'>{test.name}</h1>
                    <p className='testDescription'>{test.shortDescription}
                    <br/>{test.description}</p>
                    <input type='button' className = 'redbtn'  value = '' disabled={loading} onClick = {showTest} />
                </div>
            }

            { content === 'testStartPage' &&
                <div>
                {
                   test.questions.map((question, i) => {
                       if (i == index)
                       return (
                           <div className = 'questionCard' key = {i}>
                               <h1>{i + 1}/{test.q}</h1>
                               <p>{question}</p>

                               {
                                   i > 0 &&
                                   <input type='button' className = 'nobtn'  value = 'назад' id = {'back'+i} disabled={loading} onClick={e => back(e)}/>
                               }

                               <div className = 'answersBox'>
                               {
                                   test.answers.map((answer, j) => {
                                       return(
                                       <div className = 'registerbtn' key = {j} onClick = {(e) => handleAnswering(e, i, j)}>
                                           <p>{answer}</p>
                                       </div>)
                                   })
                               }
                                   </div>

                               {
                                   i < test.q - 1 && answers.filter(a => a.includes(i)).length > 0 &&
                                   // answers.filter(a => a.includes(i)).length > 0 &&
                                   <input type='button' className = 'nobtn'  value = 'дальше' id = {'next'+i} disabled={loading} onClick={e => next(e)}/>
                               }
                           </div>
                       )
                        })
                }
                    {/*{*/}
                    {/*    // index == test.questions - 1 &&*/}
                        {/*<input type='button' className = 'yesbtn'  value = 'результат' disabled={loading} onClick={e => saveAnswers(e)}/>*/}
                    {/*}*/}
                    {/*<input type='button' className = 'yesbtn'  value = 'результат' disabled={loading} onClick={e => saveAnswers(e)}/>*/}
                    <NavLink to = '/'
                         // to = {`/tests/${link}/${username}`}
                             disabled={loading}
                             onClick={e => saveAnswers(e)}
                    >
                        <p>Результат</p>
                    </NavLink>
                </div>
            }
        </div>


    )
}

export default TestPage;
