import React, {Component} from 'react';
import {Switch, Route, Redirect} from 'react-router-dom'
import Profile from "./Profile";
import Auth from "./Auth";
import Header from "./Header";

export const useRouts = isAuthenticated => {
    if (isAuthenticated){
        return (
            <Switch>
                <Route path = '/profile' exact> <Header/><Profile/> </Route>
                <Route path = '/tests' exact> <Header/><Profile/> </Route>
                <Redirect to = '/profile'/>
            </Switch>
        )
    }
    else {
        return (
            <Switch>
                <Route path = '/' exact> <Auth/> </Route>
                <Redirect to = '/profile'/>
            </Switch>
        )
    }
}
