import React, {useContext, useEffect, useState} from 'react';
import './Appearence/App.css';
import './Appearence/Auth.css';
import {useHttp} from "../hooks/http.hook";
import {useMessage} from "../hooks/message.hook";
import {AuthContext} from "./context/AuthContext";

const auth = require('../middleware/auth.middleware')

export const Auth = () => {
    const auth = useContext(AuthContext)
    const {loading, request, error, clearError} = useHttp();
    const [form, setForm] = useState({ login: '', password: '' })
    const message = useMessage()

    useEffect(() => {
        message(error)
        clearError()
    }, [error, message, clearError])

     const changeHandler = event => {
         setForm({...form, [event.target.name]: event.target.value})
     }

     const registerHandler = async () => {
         try {
          const data = await request('api/auth/register', 'POST', {...form})
             console.log(data.message)
         }
         catch (e) {}
     }

    const loginHandler = async () => {
        try {
            const data = await request('api/auth/login', 'POST', {...form})
            console.log(data.message)

            router.get('/', auth, async (req, res) => {
                try {
                    const links = await Link.find({ owner: req.user.userID })
                    res.json(links)
                }
                catch(e){
                    res.status(500).json({ message: "Something went wrong. Try again" })
                }
            })


            auth.login(data.token, data.userID, data.type)
            console.log('USER TYPE: ', data.type)
        }
        catch (e) {}
    }


      return (
          <div>
              <div className = 'authform'>
                  <input type='text' id = 'login' name='login' placeholder='Логин' onChange={changeHandler} value = {form.email}/>
                  <input type='password' id = 'password' name='password' placeholder='Пароль' onChange={changeHandler} value = {form.password}/>
              </div>
              {/*<input type='button' value='Войти' onClick = {registerHandler} disabled={loading}/>*/}
              <input type='button' className = 'loginbtn' onClick = {loginHandler} disabled={loading}/>
          </div>
      )
}

export default Auth
