            setTestData({
                name: '',
                link: '',
                shortDescription: '',
                description: '',
                scales: [{
                    name: '' ,
                    k: '',
                    intervals: [{
                        name: '',
                        description: '',
                        min: '',
                        max: '',
                        important: false
                    }]
                }
                ],
                questions: [''],
                answers: [''],
                scaleanswers: [{
                    k: '',
                    questions: []
                }]
            })
        }
