                                                    <p id = 'intervalP'>Интервал означает нарушение, выход за пределы нормы?</p>

                                                    <div id = 'isImportantYesContainer'>
                                                        <div id = 'isImportantYes' className = { isImportantBtn(interval.important) } onClick = { e => changeIntervalHandler(e, i, j) }/>
                                                        <p className = {textStatus(interval.important)} onClick = { e => changeIntervalHandler(e, i, j) }>Да</p>
                                                    </div>
                                                    <div id = 'isImportantNoContainer'>
                                                        <div id = 'isImportantNo' className = { isImportantBtn(!interval.important) } onClick = { e => changeIntervalHandler(e, i, j) }/>
                                                        <p className = {textStatus(!interval.important)} onClick = { e => changeIntervalHandler(e, i, j) }>Нет</p>
                                                    </div>


                                                    <textarea id = 'intervalDescription' name = 'description' value = {interval.description} placeholder = 'Комментарий' onChange = { e => changeIntervalHandler(e, i, j) }/>

                                                    <p id = 'intervalP2'>Сколько баллов по шкале нужно набрать, чтобы попасть в интервал?</p>

                                                    <div id = 'intervalMin'>
                                                        <p>От </p>
                                                        <input type = 'number' name = 'min' value = {interval.min} placeholder = 'баллов' onChange = { e => changeIntervalHandler(e, i, j) }/>
                                                    </div>

                                                    <div id = 'intervalMax'>
                                                        <p>До </p>
                                                        <input type = 'number' name = 'max' value = {interval.max} placeholder = 'баллов' onChange = { e => changeIntervalHandler(e, i, j) }/>
                                                    </div>
                                                </div>)})}
                                        <div className = 'adding' onClick = { e => handleAddingInterval(e, i) }>
                                            <AddIcon title = 'Новый интервал'/>
                                            <p className = {textStatus(false)}>Ещё один интервал</p>
                                        </div>
                                    </div>

                                    <div id = 'scaleAnswers'>
                                        {
                                            testData.answers[0] &&
                                            testData.answers.map((answer, i) => {
                                                return (
                                                    <div key = {i}>
                                                        <p>На какие вопросы нужно ответить "{answer}"?</p>
                                                        <div className = 'scaleAnswer'>
                                                            {   testData.questions[0] &&
                                                            testData.questions.map((q, j) => {
                                                                return (
                                                                    <div
                                                                        className = { isImportantBtn( testData.scaleanswers[i] && testData.scaleanswers[i].questions.includes(j)) }
                                                                        onClick = { e => changeScaleAnswersHandler(e, i, j) } key = {j}>
                                                                        <p>{(j + 1)}</p>
                                                                    </div>)
                                                            })}
                                                        </div>
                                                        {
                                                            testData.scaleanswers[i] &&
                                                            <div>
                                                                <p>На сколько будут умножаться баллы от ответа "{answer}"?</p>
                                                                <input type = 'number' name = 'k' placeholder = 'Коэффициент' min = '1' value = {testData.scaleanswers[i].k} onChange = { e => changeScaleAnswersHandler(e, i, null) }/>
                                                            </div>
                                                        }
                                                    </div>)})
                                        }
                                    </div>
                                </div>
                            )})}
                </div>

                <div id = 'scaleAddingIcon' className = 'adding' onClick = { e => handleAddingScale(e) }>
                    <AddIcon title = 'Новая шкала'/>
                    <p className = {textStatus(false)}>Ещё одна шкала</p>
                </div>
            </div>

            <div className = 'savebtn' value = 'Ок' onClick = {e => handleSaving(e)}></div>

        </div>


    )
}

export default EditTest;
